    difference() {
        cylinder(h = base_height, d = base_diameter, center = true);
        cylinder(h = base_height + 1, d = base_diameter - wall_thickness*2, center = true);
    }

    // Kettlebell body positioned on base
    translate([0, 0, base_height/2 + kettlebell_height/2 - 10]) {
        difference() {
            // Main body
            sphere(d = kettlebell_diameter);

            // Hollow interior for lighter print
            sphere(d = kettlebell_diameter - wall_thickness*2);

            // Flat bottom for stability
            translate([0, 0, -kettlebell_height/2 + 10])
                cube([kettlebell_diameter, kettlebell_diameter, kettlebell_height], center = true);
        }

        // Handle attachment points with reinforcing rings
        for (i = [0, 180]) {
            rotate([0, 0, i])
            translate([0, kettlebell_diameter/2 - 15, 0]) {
                // Reinforcement ring
                cylinder(h = 15, d = 25, center = true);
                difference() {
                    cylinder(h = 15, d = 25 - wall_thickness*2, center = true);
                    sphere(d = 25 - wall_thickness*2);
                }
            }
        }

        // Handle with ergonomic grip
        translate([0, 0, kettlebell_height/2 - 20]) {
            rotate([90, 0, 0]) {
                difference() {
                    // Main handle tube
                    cylinder(h = handle_clearance, d = handle_thickness, center = true);

                    // Grip texture - shallow grooves
                    for (i = [-handle_clearance/2 + 5 : 5 : handle_clearance/2 - 5]) {
                        translate([0, 0, i])
                        rotate([0, 90, 0])
                        cube([handle_thickness + 2, handle_thickness/2, 1], center = true);
                    }

                    // Hollow handle
                    cylinder(h = handle_clearance + 1, d = handle_thickness - wall_thickness*2, center = true);
                }
            }
        }
    }

    // Support struts connecting base to kettlebell for furniture stability
    for (angle = [0 : 120 : 240]) {
        rotate([0, 0, angle])
        translate([0, base_diameter/2 - 10, base_height/2]) {
            difference() {
                // Curved support strut
                hull() {
                    sphere(d = 8);
                    translate([0, -base_diameter/2 + 20, kettlebell_height/2])
                        sphere(d = 6);
                }

                // Hollow strut
                hull() {
                    sphere(d = 8 - wall_thickness*2);
                    translate([0, -base_diameter/2 + 20, kettlebell_height/2])
                        sphere(d = 6 - wall_thickness*2);
                }
            }
        }
    }

    // Furniture feet for stability
    for (angle = [45 : 90 : 315]) {
        rotate([0, 0, angle])
        translate([base_diameter/2 - 8, 0, -base_height/2]) {
            cylinder(h = 6, d = 16, center = true);
            translate([0, 0, -3])
            sphere(d = 16);
        }
    }
}

