// Rope Stool â€“ 3D-printable OpenSCAD model
// Seat woven from rope, four legs and stretchers
// No overhangs, 2 mm wall thickness, ~120 mm tall

// global parameters
$fn = 32;                      // cylinder resolution
rope_d = 2.2;                  // rope diameter
rope_gap = 1.0;                // gap between ropes
seat_w = 80;                   // seat width
seat_h = 12;                   // seat thickness
leg_d = 8;                     // leg diameter
leg_h = 100;                   // leg height
stretch_d = 6;                 // stretcher diameter
stretch_gap = 25;              // stretcher vertical spacing

module leg() {
    // solid cylindrical leg
    cylinder(h = leg_h, d = leg_d);
}

module stretcher(len) {
    // horizontal bar between legs
    cylinder(h = len, d = stretch_d);
}

module rope_strand(len, angle = 0) {
    // single straight rope piece
    rotate([0, 90, angle])
        cylinder(h = len, d = rope_d);
}

module seat_top() {
    // woven rope surface
    difference() {
        // outer frame
        cube([seat_w, seat_w, seat_h], center = true);

        // remove inner volume to leave 2 mm wall
        cube([seat_w - 4, seat_w - 4, seat_h + 0.2], center = true);
    }

    // simple support grid under ropes
    translate([0, 0, -seat_h/2 + 1])
        for (x = [-seat_w/2 + 8 : 16 : seat_w/2 - 8])
            for (y = [-seat_w/2 + 8 : 16 : seat_w/2 - 8])
                cube([3, 3, 2], center = true);

    // rope strands along X
    for (y = [-seat_w/2 + rope_d/2 : rope_d + rope_gap : seat_w/2 - rope_d/2])
        translate([0, y, seat_h/2 - rope_d/2])
            rope_strand(seat_w);

    // rope strands along Y
    for (x = [-seat_w/2 + rope_d/2 : rope_d + rope_gap : seat_w/2 - rope_d/2])
        translate([x, 0, seat_h/2 - rope_d/2])
            rope_strand(seat_w, 90);
}

module stool() {
    // legs
    for (x = [-1, 1])
        for (y = [-1, 1])
            translate([x * (seat_w/2 - leg_d/2), y * (seat_w/2 - leg_d/2), -leg_h/2])
                leg();

    // stretchers
    // front / back
    for (z = [-stretch_gap, stretch_gap]) {
        translate([0, (seat_w/2 - leg_d/2), z])
            rotate([90, 0, 0])
                stretcher(seat_w - leg_d);
        translate([0, -(seat_w/2 - leg_d/2), z])
            rotate([90, 0, 0])
                stretcher(seat_w - leg_d);
    }

    // left / right
    for (z = [-stretch_gap, stretch_gap]) {
        translate([(seat_w/2 - leg_d/2), 0, z])
            rotate([90, 90, 0])
                stretcher(seat_w - leg_d);
        translate([-(seat_w/2 - leg_d/2), 0, z])
            rotate([90, 90, 0])
                stretcher(seat_w - leg_d);
    }

    // seat
    translate([0, 0, leg_h/2 + seat_h/2])
        seat_top();
}

// render
stool();

