// Hurdle Furniture Item - Realistic Style, Medium Complexity
// A modern hurdle-style room divider/shelf unit

// Overall dimensions
width = 120;
height = 80;
depth = 30;

// Material thickness
thickness = 2;

// Number of horizontal slats
num_slats = 5;

// Base thickness
base_height = 8;

// Slat spacing calculation
slat_spacing = (height - base_height - thickness) / (num_slats + 1);

// Create the left vertical post
module left_post() {
    difference() {
        // Main post
        translate([thickness, 0, 0])
            cube([thickness, depth, height]);

        // Chamfered top edge
        translate([thickness, 0, height - 2])
            rotate([0, 45, 0])
                cube([sqrt(8), depth, 2]);
    }
}

// Create the right vertical post (mirrored)
module right_post() {
    mirror([1, 0, 0])
        translate([-width + thickness, 0, 0])
            left_post();
}

// Create horizontal slat
module slat(position) {
    translate([thickness * 2, 0, position])
        cube([width - thickness * 4, depth, thickness]);
}

// Create base with rounded edges
module base() {
    // Bottom plate
    translate([0, 0, 0])
        cube([width, depth, base_height]);

    // Front chamfer
    translate([0, -0.01, base_height - 2])
        rotate([45, 0, 0])
            cube([width, 2, 2]);

    // Back chamfer
    translate([0, depth, base_height - 2])
        rotate([-45, 0, 0])
            cube([width, 2, 2]);
}

// Create support braces between posts
module support_brace() {
    translate([thickness * 2, depth/2 - thickness/2, base_height])
        cube([width - thickness * 4, thickness, height - base_height - 5]);
}

// Create decorative pegs for slat joints
module peg(x, z) {
    // Vertical peg
    translate([x, depth/2 - thickness/2, z])
        cylinder(thickness, 1.5, 1.5, $fn=8);

    // Horizontal peg
    translate([x, depth/2 - thickness, z + thickness/2])
        rotate([0, 90, 0])
            cylinder(thickness, 1.5, 1.5, $fn=8);
}

// Main assembly
union() {
    // Base
    base();

    // Vertical posts
    left_post();
    right_post();

    // Support brace
    support_brace();

    // Horizontal slats with pegs
    for (i = [1:num_slats]) {
        slat_pos = base_height + slat_spacing * i;
        slat(slat_pos);

        // Add pegs at joints
        peg(thickness * 2 + 5, slat_pos);
        peg(width - thickness * 2 - 5 - thickness, slat_pos);
    }

    // Top cross member
    translate([thickness * 2, 0, height - thickness])
        cube([width - thickness * 4, depth, thickness]);

    // Front stabilizer bar
    translate([thickness * 2, -1, 0])
        cube([width - thickness * 4, 2, base_height]);

    // Back stabilizer bar
    translate([thickness * 2, depth - 1, 0])
        cube([width - thickness * 4, 2, base_height]);
}

// Cone-shaped stool with realistic furniture details
