        // Create cage pattern with longitude cuts
        for (i = [0:30:150]) {
            rotate([0, i, 0]) {
                translate([0, 0, -ball_diameter/2]) 
                    cube([ball_diameter, strut_diameter*2, ball_diameter], center = true);
            }
