        // Latitude cuts
        for (i = [0:30:180]) {
            rotate([i, 0, 0]) {
                translate([0, 0, -ball_diameter/2]) 
                    cube([ball_diameter, ball_diameter, strut_diameter*2], center = true);
            }
        }
    }

    // Seat ring structure
    difference() {
        translate([0, 0, seat_height - ball_diameter/2 + thickness]) {
            cylinder(h = thickness*2, d = ball_diameter * 0.6, center = true);
            cylinder(h = thickness*3, d = ball_diameter * 0.5, center = true);
        }
    }

    // Support struts
    for (angle = [0:120:240]) {
        rotate([0, 0, angle]) {
            translate([0, 0, -ball_diameter/2 + leg_diameter]) {
                cylinder(h = ball_diameter/2 - seat_height + leg_diameter, d = strut_diameter);
            }
