    // Armrest rings
    for (height = [seat_height + 20, seat_height + 40]) {
        translate([0, 0, height - ball_diameter/2]) {
            rotate_extrude(angle = 360, $fn = 32) {
                translate([ball_diameter * 0.3, 0, 0]) 
                    circle(d = strut_diameter);
            }
        }
    }

    // Base reinforcement ring
    translate([0, 0, -ball_diameter/2 + leg_diameter/2]) {
        cylinder(h = leg_diameter, d = ball_diameter * 0.4);
    }

    // Decorative corner brackets
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * ball_diameter * 0.25, y * ball_diameter * 0.25, -ball_diameter/2 + leg_diameter]) {
                cube([strut_diameter * 2, strut_diameter * 2, leg_diameter * 0.8], center = true);
