// Main module
module basket() {
    difference() {
        // Solid outer shell
        rounded_box(base_w, base_d, base_h, 6);

        // Hollow cavity
        translate([0, 0, wall])
            rounded_box(base_w - 2*wall, base_d - 2*wall, base_h, 4);
    }

    // Floor slats
    slats();

    // Side weave pattern
    weave();

    // Handles
    translate([0, base_d/2 - handle_r, base_h/2])
        rotate([90,0,0])
            cylinder(r=handle_r, h=base_w/2 - 10, $fn=32);
    translate([0, -(base_d/2 - handle_r), base_h/2])
        rotate([90,0,0])
            cylinder(r=handle_r, h=base_w/2 - 10, $fn=32);
}

// Rounded box primitive
module rounded_box(w, d, h, r) {
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([dx*(w/2 - r), dy*(d/2 - r), dz*(h/2 - r)])
                sphere(r=r, $fn=16);
    }
}

// Floor slats
module slats() {
    slat_w = 6;
    gap = 3;
    n = floor((base_d - 2*wall) / (slat_w + gap));
    for(i=[0:n-1]) {
        y = -base_d/2 + wall + slat_w/2 + i*(slat_w + gap);
        translate([0, y, wall/2])
            cube([base_w - 2*wall, slat_w, wall], center=true);
    }
}

// Side weave pattern (raised bands)
module weave() {
    band_w = 4;
    band_h = 1.5;
    spacing = 12;

    // Vertical bands on long sides
    for(x=[-base_w/2 + spacing : spacing : base_w/2 - spacing]) {
        translate([x, 0, base_h/2])
            cube([band_w, base_d, band_h], center=true);
    }

    // Horizontal bands on short sides
    for(y=[-base_d/2 + spacing : spacing : base_d/2 - spacing]) {
        translate([0, y, base_h/2])
            cube([base_w, band_w, band_h], center=true);
    }
}

