// Realistic T-Shaped Side Table (Tee Furniture)
// Overall dimensions: 100mm(W) x 100mm(D) x 120mm(H)

$fn = 32;

// Main table top - T shape
module table_top() {
    // Horizontal part of T
    translate([-50, -10, 110]) 
        cube([100, 20, 8]);

    // Vertical part of T
    translate([-10, -50, 110]) 
        cube([20, 100, 8]);

    // Rounded edges on top surface
    for(x = [-45, 45]) {
        translate([x, 0, 118]) 
            cylinder(h=2, r=10);
    }
    for(y = [-45, 45]) {
        translate([0, y, 118]) 
            cylinder(h=2, r=10);
    }
}

// Leg structure - 4 legs with stretchers
module legs() {
    // Define leg positions at corners of T shape
    positions = [
        [-40, -40],  // front left
        [40, -40],   // front right  
        [-40, 40],   // back left
        [40, 40]     // back right
    ];

    // Create each leg
    for(pos = positions) {
        translate([pos[0], pos[1], 0]) {
            // Main leg post
            cylinder(h=110, r=4);

            // Tapered bottom
            translate([0, 0, 0]) 
                cylinder(h=5, r1=4, r2=3);

            // Top joint detail
            translate([0, 0, 105]) 
                cylinder(h=5, r=5);
        }
    }

    // Lower stretchers between legs
    stretcher_height = 25;
    // Front
    translate([-40, -40, stretcher_height]) 
        rotate([90, 0, 0]) 
            cylinder(h=80, r=2);
    // Back
    translate([-40, 40, stretcher_height]) 
        rotate([90, 0, 0]) 
            cylinder(h=80, r=2);
    // Left
    translate([-40, -40, stretcher_height]) 
        rotate([0, 90, 0]) 
            cylinder(h=80, r=2);
    // Right
    translate([40, -40, stretcher_height]) 
        rotate([0, 90, 0]) 
            cylinder(h=80, r=2);
}

// Middle shelf for stability
module middle_shelf() {
    // T-shaped shelf at mid-height
    shelf_height = 55;

    // Horizontal bar
    translate([-35, -5, shelf_height]) 
        cube([70, 10, 3]);

    // Vertical bar
    translate([-5, -35, shelf_height]) 
        cube([10, 70, 3]);

    // Support brackets
    bracket_positions = [
        [-30, -30], [30, -30], [-30, 30], [30, 30]
    ];

    for(pos = bracket_positions) {
        translate([pos[0], pos[1], shelf_height - 5]) {
            rotate([0, 45, 0]) 
                cube([3, 3, 8]);
        }
    }
}

// Assembly
union() {
    table_top();
    legs();
    middle_shelf();
}

// Realistic medium-complexity 3D-printable furniture base
// A small side-table base with legs, apron and stretchers

// Global parameters
wall = 2;           // 2 mm wall thickness for 3D printing
scaleFactor = 1;    // Adjust overall size; ~120 mm tall when 1

// Overall dimensions (mm)
topW   = 90 * scaleFactor;
topD   = 90 * scaleFactor;
legH   = 110 * scaleFactor;
legSz  = 20 * scaleFactor;
apronH = 40 * scaleFactor;
stretchD = 15 * scaleFactor;

// Leg profile (tapered rectangle)
module taperedLeg(h, base, top) {
    linear_extrude(height = h, scale = top / base)
        square(base, center = true);
}

// Single leg with mortise recess (for apron tenon)
module leg() {
    difference() {
        union() {
            // Main leg
            translate([0, 0, legH/2])
                cube([legSz, legSz, legH], center = true);

            // Chamfered foot detail
            translate([0, 0, -0.5])
                cylinder(h = 6, r1 = legSz/2 - 1, r2 = legSz/2 + 1, $fn = 32);
        }

        // Tenon mortise (apron joint)
        translate([0, 0, legH - apronH/2])
            cube([legSz + 1, wall + 1, apronH], center = true);
    }
}

// Apron rail with integral tenon
module apron(len, axis) {
    tenonL = 8;
    rotate([0, 0, axis ? 0 : 90])
    difference() {
        union() {
            // Main rail
            translate([0, 0, apronH/2])
                cube([len - legSz*2 + tenonL*2, wall, apronH], center = true);
            // Tenons
            translate([(len - legSz*2)/2 + tenonL/2, 0, apronH/2])
                cube([tenonL, wall, apronH], center = true);
            mirror([1,0,0])
                translate([(len - legSz*2)/2 + tenonL/2, 0, apronH/2])
                    cube([tenonL, wall, apronH], center = true);
        }
        // Lightening cutout
        translate([0, 0, apronH/2])
            cube([len - legSz*2 - 10, wall + 1, apronH - 8], center = true);
    }
}

// Lower X-stretcher system
module stretcher(len) {
    offset = 4;
    h = legH - apronH - stretchD/2 - offset;
    rotate([90,0,45])
        cube([len*1.41 - legSz, wall, stretchD], center = true);
}

// Assemble complete base
module base() {
    // Four legs
    for(x = [-1,1])
        for(y = [-1,1])
            translate([x*(topW - legSz)/2, y*(topD - legSz)/2, 0])
                leg();

    // Apron rails
    translate([0, (topD - legSz)/2, legH - apronH/2])
        apron(topW, 0);
    translate([0, -(topD - legSz)/2, legH - apronH/2])
        apron(topW, 0);
    translate([(topW - legSz)/2, 0, legH - apronH/2])
        apron(topD, 1);
    translate([-(topW - legSz)/2, 0, legH - apronH/2])
        apron(topD, 1);

    // Lower stretchers in X
    stretcher(topW);
    mirror([1,0,0]) stretcher(topW);
}

// Render
base();

// Mound Furniture - Organic Grass Covered Seating Module
// Realistic style, medium complexity, 3D printable

$fa = 2; $fs = 0.5;  // Smooth curves

module mound_base() {
    // Main mound body - elliptical dome with flattened top
    scale([1.2, 1, 0.7]) 
    sphere(d=100);
}

module grass_texture() {
    // Generate grass blades across surface
    for(i=[0:15:360]) {
        for(j=[5:5:40]) {
            rotate([0, j, i])
            translate([0, 0, 50*sin(j) + 2])
                cylinder(h=3+2*(noise(i+j*0.1)), d=1.5, $fn=6);
        }
    }
}

module seating_area() {
    // Flattened top area for sitting
    difference() {
        cube([80, 60, 20], center=true);
        translate([0, 0, -10])
            cube([78, 58, 20], center=true);
    }
}

module support_frame() {
    // Internal support structure
    difference() {
        // Outer frame
        cylinder(h=45, d=30, center=true, $fn=8);

        // Hollow center
        translate([0, 0, 2])
            cylinder(h=50, d=26, center=true, $fn=8);
    }
}

module drainage_holes() {
    // Water drainage system
    for(angle=[0:120:360]) {
        rotate([0, 0, angle])
        translate([38, 0, -20])
            rotate([90, 0, 0])
                cylinder(h=8, d=3);
    }
}

module reinforcement_rings() {
    // Structural rings for stability
    for(h=[-15, 0, 15, 30]) {
        translate([0, 0, h])
            difference() {
                cylinder(h=3, d=95, center=true);
                cylinder(h=3.5, d=91, center=true);
            }
    }
}

// Main assembly
difference() {
    union() {
        // Base mound
        mound_base();

        // Add seating area
        translate([0, 0, 45])
            seating_area();

        // Support structure
        translate([0, 0, 15])
            support_frame();

        // Reinforcement
        reinforcement_rings();
    }

    // Remove bottom for printing
    translate([0, 0, -25])
        cube([150, 150, 50], center=true);

    // Drainage holes
    drainage_holes();
}

// Add grass texture on top surface
translate([0, 0, 48])
    grass_texture();

