table_top_depth = 80;
table_top_thickness = 8;
leg_length = 70;
leg_thickness = 6;

// Wall thickness for 3D printing
wall_thickness = 2;

// Main table top with rounded edges
module table_top() {
    difference() {
        // Main top surface with rounded edges
        minkowski() {
            cube([table_top_width, table_top_depth, table_top_thickness - 2]);
            cylinder(r=2, h=0.1);
        }

        // Cup holder cutouts (2 holders)
        translate([25, 20, -1]) 
            cylinder(r=15, h=table_top_thickness + 2);
        translate([95, 20, -1]) 
            cylinder(r=15, h=table_top_thickness + 2);

        // Central groove for cards
        translate([table_top_width/2, table_top_depth/2, table_top_thickness/2])
            cube([table_top_width - 40, 5, table_top_thickness + 1], center=true);
    }
}

// Folding leg mechanism
module folding_leg(x_pos, y_pos) {
    // Leg support block
    translate([x_pos, y_pos, 0]) {
        difference() {
            cube([leg_thickness + 4, leg_thickness + 4, 10]);
            // Hinge pin hole
            translate([leg_thickness/2 + 2, leg_thickness/2 + 2, 5])
                rotate([90, 0, 0])
                    cylinder(r=1.5, h=leg_thickness + 4, center=true);
        }

        // Actual leg
        translate([leg_thickness/2 + 2, leg_thickness/2 + 2, 5])
            rotate([0, 90, 0])
                difference() {
                    cube([leg_length, leg_thickness, leg_thickness]);
                    // Weight reduction holes
                    for(i = [1:3]) {
                        translate([i * 15, leg_thickness/2, leg_thickness/2])
                            cylinder(r=2, h=leg_thickness);
                    }
                }
    }
}

// Support braces between legs
module support_brace() {
    // Front brace
    translate([table_top_width/2, leg_thickness + 4, -leg_length + 5])
        cube([table_top_width - 20, wall_thickness, wall_thickness]);

    // Side braces
    rotate([0, 0, 90])
        translate([table_top_depth/2, -leg_thickness - 4, -leg_length + 5])
            cube([table_top_depth - 20, wall_thickness, wall_thickness]);
}

// Textured surface pattern
module wood_texture() {
    for(x = [5:5:table_top_width - 5]) {
        for(y = [5:5:table_top_depth - 5]) {
            translate([x, y, table_top_thickness - 0.2])
                cube([0.5, 0.5, 0.2]);
        }
    }
}

