module bench_leg(width, height, depth) {
    // Main leg post (2mm wall)
    difference() {
        cube([width, depth, height], center=true);
        translate([0, 0, 2])
            cube([width-4, depth-4, height-4], center=true);
    }
    // Foot plate
    translate([0, 0, -height/2-2])
        cube([width+6, depth+6, 4], center=true);
}

module bench_slats(count, length, width, thick, gap) {
    // Wooden slats across bench
    for (i = [0:count-1]) {
        translate([(i*(thick+gap))-((count*(thick+gap))/2)+thick/2, 0, 0])
            cube([thick, width, length], center=true);
    }
}

module bench_support(length, width, height) {
    // Horizontal support beam
    difference() {
        cube([length, width, height], center=true);
        translate([0, 0, 2])
            cube([length-4, width-4, height-4], center=true);
    }
}

module bench_back(count, length, width, thick, gap) {
    // Backrest slats
    for (i = [0:count-1]) {
        translate([(i*(thick+gap))-((count*(thick+gap))/2)+thick/2, 0, 20])
            rotate([10, 0, 0])
                cube([thick, width, length], center=true);
    }
}

