// 1:12 scale sectional track module with realistic sleeper/ rail detail
// External footprint: 150 mm long x 100 mm wide x 12 mm thick printable slab
// No over-hangs, 2 mm wall everywhere, ready for FDM printing

$fn = 32; // smooth curves

// ---- global dimensions ----
module_len = 150;
module_w   = 100;
module_h   = 12;          // overall slab thickness

rail_h      = 3.5;        // rail height above sleeper tops
rail_w      = 2.2;        // rail head width
rail_web_t  = 1.2;        // web thickness
rail_base_w = 3.0;        // foot width

sleeper_w   = 10;         // tie width
sleeper_t   = 2.2;        // tie thickness (above slab)
sleeper_gap = 18;         // centre-to-centre spacing
sleeper_len = module_w;   // full width of module

ballast_h   = 1.8;        // crushed-stone layer height above slab

// ---- main geometry ----
difference() {
    union() {
        // ---- base slab ----
        translate([0,0,-module_h/2])
            cube([module_len,module_w,module_h], center=true);

        // ---- ballast mound ----
        translate([0,0,-module_h/2+ballast_h/2])
            cube([module_len,module_w,ballast_h], center=true);

        // ---- sleepers (ties) ----
        sleeper_positions = [ -60, -42, -24, -6, 12, 30, 48, 66 ];
        for (x = sleeper_positions)
            translate([x,0,ballast_h-sleeper_t/2-module_h/2])
                cube([sleeper_t,sleeper_len,sleeper_t], center=true);

        // ---- left rail ----
        rail_path(-25);

        // ---- right rail ----
        rail_path(25);
    }

    // ---- clearance cut-outs for printing (avoid elephant foot) ----
    // underside chamfer
    translate([0,0,-module_h/2+0.5])
        cube([module_len+2,module_w+2,module_h-1], center=true);
}

// ---- rail profile module ----
module rail_path(offset_y) {
    for (x = [-module_len/2+5 : sleeper_gap : module_len/2-5]) {
        translate([x,offset_y,-module_h/2+ballast_h+sleeper_t])
            linear_extrude(height = rail_h)
                rail_profile();
    }
}

// ---- realistic rail cross-section ----
module rail_profile() {
    difference() {
        union() {
            // head
            square([rail_w,rail_h*0.4], center=true);
            translate([0,-rail_h*0.2,0])
                square([rail_web_t,rail_h*0.4], center=true);
            // foot
            translate([0,-rail_h*0.5,0])
                square([rail_base_w,rail_h*0.2], center=true);
        }
        // relieve inside web for printing
        translate([0,-rail_h*0.3,0])
            square([rail_web_t+0.4,rail_h*0.25], center=true);
    }
}

