// Pool chair - realistic style, medium complexity
// Dimensions: 120 x 60 x 85 mm (L x W x H)

module pool_chair() {
    // Frame thickness
    frame_t = 2;

    // Seat dimensions
    seat_w = 60;
    seat_d = 50;
    seat_h = 40;

    // Backrest dimensions
    back_h = 45;
    back_angle = 15;

    // Leg height
    leg_h = 38;

    // Armrest height
    arm_h = 20;

    // Seat slats
    slat_w = 4;
    slat_gap = 3;
    num_slats = floor(seat_d / (slat_w + slat_gap));

    // Backrest slats
    back_slats = 8;

    // Frame structure
    union() {
        // Left frame
        translate([-seat_w/2 + frame_t, 0, 0])
            cube([frame_t, seat_d, seat_h + leg_h]);

        // Right frame
        translate([seat_w/2 - frame_t, 0, 0])
            cube([frame_t, seat_d, seat_h + leg_h]);

        // Front frame
        translate([-seat_w/2 + frame_t, -seat_d/2 + frame_t, 0])
            cube([seat_w - 2*frame_t, frame_t, leg_h]);

        // Back frame
        translate([-seat_w/2 + frame_t, seat_d/2 - frame_t, 0])
            cube([seat_w - 2*frame_t, frame_t, leg_h + seat_h]);

        // Seat support bars
        for(i = [1:3]) {
            translate([-seat_w/2 + frame_t, -seat_d/2 + i*(seat_d/4), leg_h])
                cube([seat_w - 2*frame_t, frame_t, frame_t]);
        }

        // Back support bar
        rotate([back_angle, 0, 0])
            translate([0, seat_d/2, leg_h + seat_h - 5])
                cube([seat_w - 2*frame_t, frame_t, frame_t], center=true);
    }

    // Seat slats
    for(i = [0:num_slats-1]) {
        y = -seat_d/2 + i*(slat_w + slat_gap) + slat_w/2;
        translate([0, y, leg_h + frame_t])
            cube([seat_w - 2*frame_t - 4, slat_w, frame_t], center=true);
    }

    // Backrest slats
    for(i = [0:back_slats-1]) {
        x = -seat_w/2 + frame_t + 2 + i*(seat_w - 2*frame_t - 4)/(back_slats-1);
        translate([x, 0, 0])
            rotate([back_angle, 0, 0])
                translate([0, seat_d/2, leg_h + seat_h + back_h/2])
                    cube([frame_t, frame_t, back_h - frame_t], center=true);
    }

    // Armrests
    // Left armrest
    translate([-seat_w/2 - frame_t, -seat_d/2 + 10, leg_h + seat_h/2])
        union() {
            // Support post
            cube([frame_t, frame_t, arm_h]);

            // Armrest top
            translate([0, 0, arm_h])
                cube([frame_t + 5, 15, frame_t]);
        }

    // Right armrest
    mirror([1, 0, 0])
        translate([-seat_w/2 - frame_t, -seat_d/2 + 10, leg_h + seat_h/2])
            union() {
                // Support post
                cube([frame_t, frame_t, arm_h]);

                // Armrest top
                translate([0, 0, arm_h])
                    cube([frame_t + 5, 15, frame_t]);
            }

    // Front legs
    translate([-seat_w/2 + frame_t/2, -seat_d/2 + frame_t/2, 0])
        cylinder(h=leg_h, r=frame_t, $fn=8);

    translate([seat_w/2 - frame_t/2, -seat_d/2 + frame_t/2, 0])
        cylinder(h=leg_h, r=frame_t, $fn=8);

    // Back legs (taller)
    translate([-seat_w/2 + frame_t/2, seat_d/2 - frame_t/2, 0])
        cylinder(h=leg_h + seat_h, r=frame_t, $fn=8);

    translate([seat_w/2 - frame_t/2, seat_d/2 - frame_t/2, 0])
        cylinder(h=leg_h + seat_h, r=frame_t, $fn=8);
}

// Render the chair
pool_chair();

