module leg(x,y){
    translate([x,y,-leg_h]) 
        difference(){
            cube([leg_sz,leg_sz,leg_h]);               // solid leg
            translate([wall,wall,-1])                   // hollow center
                cube([leg_sz-2*wall, leg_sz-2*wall, leg_h+2]);
        }
}

module legs(){
    for(dx=[0,seat_w-leg_sz], dy=[0,seat_d-leg_sz])
        leg(dx,dy);
}

module seat(){
    difference(){
        cube([seat_w,seat_d,seat_t]);                   // solid seat
        translate([wall,wall,-1])                       // hollow underneath
            cube([seat_w-2*wall, seat_d-2*wall, seat_t+2]);
    }
}

// backrest slats
slat_w = seat_w/5;
slat_h = 35;
slat_t = wall;

module back_slat(i){
    translate([i*slat_w,0,seat_t])
        difference(){
            cube([slat_t,seat_d,slat_h]);
            translate([wall,wall,-1])
                cube([slat_t-2*wall, seat_d-2*wall, slat_h+2]);
        }
}

module backrest(){
    for(i=[0:4])
        back_slat(i);
}

// TRAP mechanism â€“ spring-loaded bar hidden under seat
bar_w = seat_w - 4*wall;
bar_t = 4;
spring_coils = 5;
spring_r = 4;

module spring(){
    rotate([90,0,0])
        for(i=[0:spring_coils]){
            translate([0,0,i*2*spring_r/spring_coils])
                rotate([0,0,i*360*2/spring_coils])
                    cylinder(h=2, r1=spring_r, r2=spring_r, $fn=8);
        }
}

module trap_bar(){
    translate([2*wall,seat_d/2-bar_t/2,-leg_h/2])
        union(){
            difference(){
                cube([bar_w,bar_t,leg_h/2]);                // solid bar
                translate([wall,wall,-1])
                    cube([bar_w-2*wall, bar_t-2*wall, leg_h/2+2]);
            }
            translate([bar_w/2, bar_t/2, -spring_r])
                spring();
        }
}

// Assemble chair with hidden trap
union(){
    legs();
    translate([0,0,seat_t]) seat();
    backrest();
    trap_bar();
}

