            cube([leg_thickness, leg_thickness, leg_height], center=true);

        // Back leg
        translate([x_pos, seat_width/2 - leg_thickness/2, leg_height/2]) 
            cube([leg_thickness, leg_thickness, leg_height], center=true);

        // Stretcher
        translate([x_pos, 0, leg_height/4]) 
            cube([leg_thickness, seat_width - leg_thickness, leg_thickness], center=true);
    }

    // Left legs
    leg_assembly(-seat_length/2 + leg_thickness/2);

    // Right legs
    leg_assembly(seat_length/2 - leg_thickness/2);

    // Backrest
    translate([0, seat_width/2 + back_thickness/2, leg_height + back_height/2]) {
        // Main back frame
        difference() {
            cube([seat_length, back_thickness, back_height], center=true);

            // Create curved top edge
            translate([0, 0, back_height/2 - 2]) {
                for (i = [-seat_length/2+5:5:seat_length/2-5]) {
                    translate([i, 0, 0]) 
                        rotate([90, 0, 0]) 
                        cylinder(h=back_thickness+1, r=3, center=true);
