            translate([x_pos, 0, 0]) 
                cube([slat_width, back_thickness, back_height - 5], center=true);
        }
    }

    // Support braces under seat
    translate([0, 0, leg_height - 3]) {
        rotate([90, 0, 0]) 
            cylinder(h=seat_length - leg_thickness*2, r=2, center=true);
    }

    // Corner braces
    module corner_brace(x_pos, y_pos) {
        translate([x_pos, y_pos, leg_height - 5]) {
            difference() {
                cube([6, 6, 10], center=true);
                cylinder(h=11, r=2, center=true);
            }
        }
    }

    corner_brace(-seat_length/2 + 6, -seat_width/2 + 6);
    corner_brace(seat_length/2 - 6, -seat_width/2 + 6);
