// Rough wooden stool - Realistic style, medium complexity
// Dimensions: 120mm height, 250mm width, 250mm depth

module leg(x, y, angle) {
    // Create rough wooden leg with taper
    translate([x, y, 0]) {
        rotate([0, 0, angle]) {
            // Main leg body with slight taper
            difference() {
                hull() {
                    translate([0, 0, 0])
                        cylinder(h=110, d1=20, d2=18, $fn=8);
                    translate([0, 0, 110])
                        cylinder(h=10, d1=18, d2=22, $fn=8);
                }

                // Add notches for rough texture
                for(i = [10:20:100]) {
                    translate([0, 0, i])
                        rotate([0, 0, i*15])
                        cube([25, 2, 3], center=true);
                }
            }

            // Foot detail
            translate([0, 0, -2])
                cylinder(h=4, d=24, $fn=6);
        }
    }
}

module stretcher(x1, y1, x2, y2, z) {
    // Create connecting stretcher between legs
    length = sqrt(pow(x2-x1, 2) + pow(y2-y1, 2));
    angle = atan2(y2-y1, x2-x1);

    translate([(x1+x2)/2, (y1+y2)/2, z]) {
        rotate([0, 0, angle]) {
            hull() {
                translate([-length/2, 0, 0])
                    cylinder(h=2, d=12, $fn=6);
                translate([length/2, 0, 0])
                    cylinder(h=2, d=12, $fn=6);
            }
        }
    }
}

module seat() {
    // Create rough wooden seat with irregular shape
    translate([0, 0, 110]) {
        difference() {
            // Main seat
            hull() {
                for(i = [0:45:359]) {
                    r = 110 + (i % 2 == 0 ? 5 : -5);
                    translate([r*cos(i), r*sin(i), 0])
                        cylinder(h=15, d=20, $fn=8);
                }
            }

            // Carve out sitting area
            translate([0, 0, 8])
                scale([0.85, 0.85, 1])
                cylinder(h=20, d=200, $fn=32);

            // Add random knot holes
            for(i = [0:30:330]) {
                translate([40*cos(i), 40*sin(i), 5])
                    cylinder(h=15, d=8+sin(i*5)*3, $fn=6);
            }
        }

        // Edge detail
        for(i = [0:10:359]) {
            rotate([0, 0, i])
            translate([95, 0, 7])
                cube([15, 3, 8]);
        }
    }
}

// Assembly
union() {
    // Create four legs
    leg(-80, -80, 5);
    leg(80, -80, -5);
    leg(-80, 80, -5);
    leg(80, 80, 5);

    // Add stretchers for stability
    stretcher(-80, -80, 80, -80, 45);
    stretcher(-80, 80, 80, 80, 45);
    stretcher(-80, -80, -80, 80, 45);
    stretcher(80, -80, 80, 80, 45);

    // Diagonal stretchers
    stretcher(-80, -80, 80, 80, 70);
    stretcher(80, -80, -80, 80, 70);

    // Seat
    seat();
}

