table_depth = 80;
table_height = 45;
wall_thickness = 2;

// Leg parameters
leg_size = 8;
leg_height = table_height - wall_thickness;

// Create main table body
module table_top() {
    // Main tabletop
    difference() {
        // Solid top
        cube([table_width, table_depth, wall_thickness], center=true);

        // Create divot pattern - organic random depressions
        for (i = [0:20]) {
            // Random position for each divot
            x_pos = (rands(-table_width/2 + 10, table_width/2 - 10, 1))[0];
            y_pos = (rands(-table_depth/2 + 10, table_depth/2 - 10, 1))[0];

            // Varying sizes for natural look
            divot_rad = (rands(3, 8, 1))[0];
            divot_depth = (rands(0.5, 2, 1))[0];

            translate([x_pos, y_pos, wall_thickness/2 - divot_depth/2]) {
                // Elliptical divot for natural wood grain appearance
                scale([1.2, 0.8, 1])
                sphere(r=divot_rad);
            }
        }
    }
}

// Create table legs with slight taper
module table_leg(x_pos, y_pos) {
    // Leg with rounded edges
    difference() {
        // Main leg body
        translate([x_pos, y_pos, -leg_height/2]) {
            cylinder(h=leg_height, r1=leg_size/2, r2=leg_size/2 - 1, center=true);
        }

        // Chamfer bottom edge for printability
        translate([x_pos, y_pos, -leg_height + 2]) {
            cylinder(h=3, r1=leg_size/2 + 1, r2=leg_size/2, center=true);
        }
    }
}

// Create support frame
module support_frame() {
    // Front support
    translate([0, table_depth/2 - leg_size/2, -leg_height/2 + 5]) {
        cube([table_width - leg_size, wall_thickness, leg_size], center=true);
    }

    // Back support
    translate([0, -table_depth/2 + leg_size/2, -leg_height/2 + 5]) {
        cube([table_width - leg_size, wall_thickness, leg_size], center=true);
    }

    // Side supports
    translate([table_width/2 - leg_size/2, 0, -leg_height/2 + 5]) {
        cube([wall_thickness, table_depth - leg_size, leg_size], center=true);
    }

    translate([-table_width/2 + leg_size/2, 0, -leg_height/2 + 5]) {
        cube([wall_thickness, table_depth - leg_size, leg_size], center=true);
    }
}

// Main assembly
module divot_table() {
    // Position tabletop at correct height
    translate([0, 0, leg_height + wall_thickness/2]) {
        table_top();
    }

    // Add legs at corners
    for (x = [-table_width/2 + leg_size/2, table_width/2 - leg_size/2]) {
        for (y = [-table_depth/2 + leg_size/2, table_depth/2 - leg_size/2]) {
            table_leg(x, y);
        }
    }

    // Add support frame for stability
    support_frame();

    // Add subtle edge rounding on tabletop
    translate([0, 0, leg_height + wall_thickness]) {
        difference() {
            cube([table_width + 0.5, table_depth + 0.5, wall_thickness/2], center=true);
            cube([table_width - 1, table_depth - 1, wall_thickness], center=true);
        }
    }
}

