        translate([0, -flag_height/2, pole_diameter/2])
            cylinder(h=flag_height, d=pole_diameter+2, $fn=32);

    // Main flag body with curved profile
    translate([pole_diameter/2+1, 0, flag_height-15])
        rotate([0, 85, 0])
            linear_extrude(height=2)
                polygon(points=[
                    [0, 0],
                    [flag_width, 0],
                    [flag_width-5, -flag_height/4],
                    [flag_width-8, -flag_height/2],
                    [flag_width-5, -flag_height*3/4],
                    [flag_width, -flag_height],
                    [0, -flag_height]
                ]);

    // Flag reinforcement stitching
    for(pos = [5, flag_height-5]) {
        translate([pole_diameter/2+1, -1, flag_height-15-pos])
            cube([2, 3, 1]);
    }
}

// Ground spike for stability
