// Realistic Teebox Furniture â€“ OpenSCAD
// Wall-thickness 2 mm, printable without supports
// Overall footprint 120 mm Ã— 85 mm Ã— 105 mm

$fn = 36;          // Smooth curves

// ---------- parameters ----------
body_w = 120;
body_d = 85;
body_h = 60;
wall_t = 2;        // uniform wall thickness
leg_h  = 45;       // from bottom of body to ground
leg_t  = wall_t;

// ---------- modules ----------
module rounded_box(w, d, h, r, thick) {
    // hollow box with rounded vertical edges
    outer = cube([w, d, h], center = true);
    inner = cube([w-2*thick, d-2*thick, h+1], center = true);
    translate([0, 0, -0.5]) difference() {
        outer;
        inner;
    }
}

module leg_profile() {
    // simple rectangular leg cross-section
    square([leg_t, leg_t], center = true);
}

module leg(x, y) {
    translate([x, y, -leg_h/2 - body_h/2])
        linear_extrude(height = leg_h, center = true)
            leg_profile();
}

module tee_slot() {
    // elliptical tee slot on top surface
    slot_w = 10;
    slot_l = 18;
    slot_d = 3;
    translate([0, 0, body_h/2 + 0.1]) {
        rotate([90, 0, 0])
            cylinder(h = slot_l, d1 = slot_w, d2 = slot_w*0.7, center = true);
    }
}

module handle() {
    // simple cut-out handle on front face
    h_w = 30;
    h_h = 8;
    h_d = 15;
    translate([0, body_d/2 + 0.1, 0])
        rotate([0, 90, 0])
            cylinder(h = h_w, d = h_h, center = true);
}

// ---------- main geometry ----------
difference() {
    // main body
    union() {
        rounded_box(body_w, body_d, body_h, 5, wall_t);

        // four legs
        dx = body_w/2 - leg_t;
        dy = body_d/2 - leg_t;
        leg( dx,  dy);
        leg(-dx,  dy);
        leg( dx, -dy);
        leg(-dx, -dy);
    }

    // subtract tee slot
    tee_slot();

    // subtract front handle
    handle();
}

// ---------- optional printed ball tray ----------
translate([0, 0, -leg_h - 3 - 1]) {
    tray_w = body_w - 20;
    tray_d = body_d - 20;
    tray_h = 3;
    difference() {
        cube([tray_w, tray_d, tray_h], center = true);
        translate([0, 0, -1])
            cube([tray_w-2*wall_t, tray_d-2*wall_t, tray_h+2], center = true);
    }
}

// Scoreboard Furniture - Medium Complexity
// Realistic style with structural frame and display panels

// Overall dimensions
overall_width = 100;
overall_height = 70;
overall_depth = 30;

// Wall thickness for 3D printing
wall_thickness = 2;

// Frame dimensions
frame_width = overall_width - 4;
frame_height = overall_height - 4;
frame_depth = overall_depth - 2;

// Display panel dimensions
display_width = frame_width - 8;
display_height = frame_height - 8;
display_depth = 1;

// Leg dimensions
leg_width = 4;
leg_height = 40;
leg_depth = 4;

// Score digit dimensions
digit_width = 8;
digit_height = 12;
digit_depth = 1;

// Main frame structure
module frame() {
    // Outer frame
    difference() {
        cube([frame_width, frame_height, frame_depth], center=true);
        translate([0, 0, wall_thickness/2])
            cube([frame_width-wall_thickness*2, frame_height-wall_thickness*2, frame_depth+1], center=true);
    }

    // Frame edges with rounded profile
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x*(frame_width/2-wall_thickness/2), y*(frame_height/2-wall_thickness/2), 0])
                cylinder(h=frame_depth, r=wall_thickness, center=true, $fn=8);
        }
    }
}

// Display panel with recessed area
module display_panel() {
    // Main panel
    translate([0, 0, frame_depth/2 + display_depth/2])
        cube([display_width, display_height, display_depth], center=true);

    // Recessed display area
    translate([0, 0, frame_depth/2 + display_depth])
        cube([display_width-4, display_height-4, 0.5], center=true);
}

// Support legs with cross-bracing
module legs() {
    // Front legs
    for(x = [-1, 1]) {
        translate([x*(frame_width/2-leg_width/2-2), 0, -leg_height/2-frame_depth/2])
            cube([leg_width, leg_depth, leg_height], center=true);
    }

    // Back legs
    for(x = [-1, 1]) {
        translate([x*(frame_width/2-leg_width/2-2), frame_height/2-leg_depth/2-2, -leg_height/2-frame_depth/2])
            cube([leg_width, leg_depth, leg_height], center=true);
    }

    // Cross brace between front legs
    translate([0, 0, -leg_height/2-frame_depth/2+5])
        cube([frame_width-8, leg_width, leg_width], center=true);
}

// Score digits (7-segment style)
module seven_segment_digit(pos_x, pos_y, value) {
    translate([pos_x, pos_y, frame_depth/2 + display_depth + 0.5]) {
        // Top horizontal segment
        if(value == 0 || value == 2 || value == 3 || value == 5 || value == 6 || value == 7 || value == 8 || value == 9)
            cube([digit_width, digit_height/5, digit_depth], center=true);

        // Top-left vertical segment
        if(value == 0 || value == 4 || value == 5 || value == 6 || value == 8 || value == 9)
            translate([-digit_width/3, digit_height/6, 0])
                cube([digit_width/5, digit_height/3, digit_depth], center=true);

        // Top-right vertical segment
        if(value == 0 || value == 1 || value == 2 || value == 3 || value == 4 || value == 7 || value == 8 || value == 9)
            translate([digit_width/3, digit_height/6, 0])
                cube([digit_width/5, digit_height/3, digit_depth], center=true);

        // Middle horizontal segment
        if(value == 2 || value == 3 || value == 4 || value == 5 || value == 6 || value == 8 || value == 9)
            translate([0, 0, 0])
                cube([digit_width, digit_height/5, digit_depth], center=true);

        // Bottom-left vertical segment
        if(value == 0 || value == 2 || value == 6 || value == 8)
            translate([-digit_width/3, -digit_height/6, 0])
                cube([digit_width/5, digit_height/3, digit_depth], center=true);

        // Bottom-right vertical segment
        if(value == 0 || value == 1 || value == 3 || value == 4 || value == 5 || value == 6 || value == 7 || value == 8 || value == 9)
            translate([digit_width/3, -digit_height/6, 0])
                cube([digit_width/5, digit_height/3, digit_depth], center=true);

        // Bottom horizontal segment
        if(value == 0 || value == 2 || value == 3 || value == 5 || value == 6 || value == 8 || value == 9)
            translate([0, -digit_height/2.5, 0])
                cube([digit_width, digit_height/5, digit_depth], center=true);
    }
}

// Control buttons
module control_buttons() {
    // Button panel
    translate([0, -display_height/2-5, frame_depth/2 + display_depth/2])
        cube([display_width/2, 6, display_depth], center=true);

    // Buttons
    for(i = [-1, 0, 1]) {
        translate([i*8, -display_height/2-5, frame_depth/2 + display_depth + 1])
            cylinder(h=2, r=2, $fn=16);
    }
}

// Main assembly
union() {
    // Structural frame
    frame();

    // Display panel
    display_panel();

    // Score digits for home and away teams
    seven_segment_digit(-display_width/4, display_height/4, 7);
    seven_segment_digit(display_width/4, display_height/4, 5);
    seven_segment_digit(-display_width/4, -display_height/4, 3);
    seven_segment_digit(display_width/4, -display_height/4, 2);

    // Control buttons
    control_buttons();

    // Support legs
    legs();
}

