// Realistic Medium-Complexity Clock Furniture
// Wall-mounted mantel clock with frame and traditional face

// Overall dimensions
clock_diameter = 100;
clock_depth = 25;
frame_thickness = 8;
wall_mount_thickness = 5;

module clock_frame() {
    // Outer frame ring
    difference() {
        cylinder(h=frame_thickness, d=clock_diameter + 15, center=true);
        cylinder(h=frame_thickness+1, d=clock_diameter + 15 - 4, center=true);
    }

    // Back panel
    translate([0, 0, -frame_thickness/2 - 1]) {
        difference() {
            cylinder(h=2, d=clock_diameter + 15);
            cylinder(h=3, d=clock_diameter - 2);
        }
    }
}

module clock_face() {
    // Main face
    difference() {
        cylinder(h=2, d=clock_diameter);
        cylinder(h=3, d=clock_diameter - 4);
    }

    // Number markers
    for(i=[1:12]) {
        angle = i * 30;
        radius = (clock_diameter/2) - 8;
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        translate([x, y, 1.1]) {
            rotate([0, 0, angle + 90]) {
                cube([1, 3, 1.5], center=true);
            }
        }
    }

    // Center dot
    translate([0, 0, 1.5]) {
        cylinder(h=2, d=4);
    }
}

module clock_hands() {
    // Hour hand
    translate([0, 0, 2]) {
        rotate([0, 0, -30]) {
            cube([3, clock_diameter/4, 1], center=true);
        }
    }

    // Minute hand
    translate([0, 0, 3]) {
        rotate([0, 0, 60]) {
            cube([2, clock_diameter/3, 1], center=true);
        }
    }

    // Second hand
    translate([0, 0, 4]) {
        rotate([0, 0, 180]) {
            cube([1, clock_diameter/2.5, 0.5], center=true);
        }
    }
}

module wall_mount() {
    // Back mounting bracket
    translate([0, -clock_diameter/2 - 10, 0]) {
        cube([30, 15, wall_mount_thickness], center=true);
    }

    // Keyhole slots for hanging
    translate([0, -clock_diameter/2 - 10, wall_mount_thickness/2]) {
        for(x=[-8, 8]) {
            translate([x, 0, 0]) {
                difference() {
                    cylinder(h=wall_mount_thickness+1, d=4, center=true);
                    translate([0, 0, -wall_mount_thickness/2 - 1]) {
                        cylinder(h=wall_mount_thickness+2, d=2);
                    }
                }
            }
        }
    }
}

module decorative_details() {
    // Top decorative finial
    translate([0, 0, frame_thickness/2 + 2]) {
        cylinder(h=8, d=6);
        translate([0, 0, 8]) {
            sphere(d=6);
        }
    }

    // Side ornaments
    for(x=[-1, 1]) {
        translate([x * (clock_diameter/2 + 5), 0, 0]) {
            rotate([0, 90, 0]) {
                cylinder(h=10, d=5);
            }
        }
    }
}

// Assembly
union() {
    clock_frame();

    translate([0, 0, frame_thickness/2 + 1]) {
        clock_face();
        clock_hands();
    }

    translate([0, 0, -clock_depth/2 - wall_mount_thickness/2]) {
        wall_mount();
    }

    translate([0, 0, frame_thickness/2]) {
        decorative_details();
    }
}

// Realistic Timer Furniture - Medium Complexity
// A wooden-look timer with hourglass chamber, frame, and base

$fa = 2;
$fs = 0.5;

// Overall dimensions
