base_depth = 60;
base_height = 15;
frame_height = 100;
glass_diameter = 30;
glass_thickness = 2;
post_width = 8;

// Base module with rounded edges
module base() {
    difference() {
        // Main base block
        translate([0, 0, base_height/2])
            minkowski() {
                cube([base_width, base_depth, base_height], center=true);
                sphere(3);
            }

        // Hollow bottom for printability
        translate([0, 0, base_height - 2])
            cube([base_width - 4, base_depth - 4, base_height], center=true);

        // Center hole for frame
        translate([0, 0, -1])
            cylinder(h=base_height + 2, d=post_width + 1, center=true);
    }
}

// Frame posts
module frame_post() {
    // Main post
    cylinder(h=frame_height, d=post_width, center=true);

    // Top connector
    translate([0, 0, frame_height/2 + post_width/2])
        rotate([90, 0, 0])
            cylinder(h=post_width + 4, d=post_width, center=true);

    // Bottom connector
    translate([0, 0, -frame_height/2 - post_width/2])
        rotate([90, 0, 0])
            cylinder(h=post_width + 4, d=post_width, center=true);
}

// Hourglass chamber
module hourglass() {
    // Top glass bulb
    difference() {
        sphere(d=glass_diameter);
        sphere(d=glass_diameter - glass_thickness*2);
    }

    // Bottom glass bulb
    translate([0, 0, -glass_diameter])
        difference() {
            sphere(d=glass_diameter);
            sphere(d=glass_diameter - glass_thickness*2);
        }

    // Neck connection
    translate([0, 0, -glass_diameter/2])
        difference() {
            cylinder(h=glass_diameter, d=glass_diameter/3, center=true);
            cylinder(h=glass_diameter + 2, d=glass_diameter/3 - glass_thickness*2, center=true);
        }

    // Sand simulation - top half
    translate([0, 0, glass_diameter/4])
        cylinder(h=glass_diameter/2, d=glass_diameter - glass_thickness*4, center=true);

    // Sand simulation - bottom pile
    translate([0, 0, -glass_diameter - glass_diameter/4])
        sphere(d=glass_diameter/2);
}

// Support rings
module support_ring(height) {
    difference() {
        cylinder(h=5, d=glass_diameter + 8, center=true);
        cylinder(h=7, d=glass_diameter + 2, center=true);
    }
}

// Assembly
union() {
    // Base
    base();

    // Frame posts - front and back
    translate([base_width/2 - post_width, 0, base_height/2 + frame_height/2])
        frame_post();
    translate([-base_width/2 + post_width, 0, base_height/2 + frame_height/2])
        frame_post();

    // Top cross bar
    translate([0, 0, base_height + frame_height - post_width/2])
        rotate([90, 0, 0])
            cylinder(h=base_width - post_width*2, d=post_width, center=true);

    // Bottom support rings
    translate([0, 0, base_height + frame_height/3])
        support_ring(0);
    translate([0, 0, base_height + frame_height*2/3])
        support_ring(0);

    // Hourglass assembly
    translate([0, 0, base_height + frame_height/2])
        hourglass();
}

// Decorative frame details
module frame_detail() {
    for(i = [0:1]) {
        rotate([0, 0, i*90])
            translate([base_width/2 - post_width - 2, 0, base_height + frame_height/2])
                cylinder(h=3, d=6, center=true);
    }
}

frame_detail();

// Horn Furniture - Realistic Style, Medium Complexity
// A stylized horn chair with organic curves and structural elements

$fn = 50;

// Main parameters
horn_length = 120;
horn_base_diameter = 40;
horn_tip_diameter = 5;
wall_thickness = 2;

// Chair proportions
seat_height = 45;
back_height = 80;
leg_height = 40;

module horn_curve(t) {
    // Create a natural horn curve using parametric equation
    x = horn_length * t * 0.8;
    y = 30 * sin(t * 180);
    z = 20 * sin(t * 270);
    [x, y, z];
}

module horn_shell() {
    // Main horn body with hollow interior
    difference() {
        // Outer shell
        hull() {
            for (i = [0:0.1:1]) {
                pos = horn_curve(i);
                translate(pos)
                    sphere(d = lerp(horn_base_diameter, horn_tip_diameter, i));
            }
        }

        // Inner hollow
        hull() {
            for (i = [0:0.1:1]) {
                pos = horn_curve(i);
                translate(pos)
                    sphere(d = lerp(horn_base_diameter - wall_thickness*2, 
                                 horn_tip_diameter - wall_thickness*2, i));
            }
        }
    }
}

module horn_chair() {
    // Main chair structure using horns as legs and back

    // Seat platform
    difference() {
        translate([0, 0, leg_height])
            cube([80, 60, wall_thickness], center=true);

        // Lightening holes
        for (x = [-25, 0, 25])
            for (y = [-15, 0, 15])
                translate([x, y, leg_height])
                    cylinder(h=wall_thickness+1, d=8, center=true);
    }

    // Front horns as legs
    // Left front leg
    translate([-25, 25, leg_height]) {
        rotate([90, 0, -15])
            scale([1, 1, 0.8])
                horn_shell();
    }

    // Right front leg
    translate([25, 25, leg_height]) {
        rotate([90, 0, 15])
            scale([1, 1, 0.8])
                horn_shell();
    }

    // Rear horns as back support
    // Left rear
    translate([-25, -25, leg_height]) {
        rotate([105, 0, -20])
            horn_shell();
    }

    // Right rear
    translate([25, -25, leg_height]) {
        rotate([105, 0, 20])
            horn_shell();
    }

    // Backrest connecting horns
    translate([0, -25, leg_height + 60])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=wall_thickness, d1=70, d2=60, center=true);
            cylinder(h=wall_thickness+1, d1=66, d2=56, center=true);
        }

    // Side support beams
    for (side = [-1, 1]) {
        translate([side * 40, 0, leg_height + 20])
            rotate([0, 90, 0])
            difference() {
                cylinder(h=50, d=12, center=true);
                cylinder(h=51, d=8, center=true);
            }
    }

    // Front support beam
    translate([0, 30, leg_height + 15])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=60, d=10, center=true);
            cylinder(h=61, d=6, center=true);
        }
}

// Helper function for interpolation
function lerp(a, b, t) = a + (b - a) * t;

// Render the chair
horn_chair();

