// Rounded box helper
module roundedBox(l, w, h, r) {
    hull() {
        for(x=[-1,1], y=[-1,1], z=[-1,1])
            translate([x*(l/2-r), y*(w/2-r), z*(h/2-r)])
                sphere(r=r);
    }
