// Metal band around the chest
module band() {
    // Front band
    translate([0, 35 - bandWidth/2, 30])
        cube([100, bandWidth, 60], center=true);

    // Back band
    translate([0, -35 + bandWidth/2, 30])
        cube([100, bandWidth, 60], center=true);

