// Hawk Furniture - Realistic Style, Medium Complexity
// 3D printable hawk-shaped furniture piece with structural elements

// Main parameters
wing_span = 120;
body_length = 80;
body_height = 35;
wall_thickness = 2;

// Main body module
module hawk_body() {
    // Central body structure
    difference() {
        hull() {
            // Main torso
            translate([0, 0, body_height/2])
                scale([1, 0.7, 0.8])
                sphere(d=body_height, $fn=20);

            // Tail section
            translate([-body_length/3, 0, body_height/3])
                scale([1.2, 0.5, 0.4])
                sphere(d=body_height*0.8, $fn=16);
        }

        // Hollow interior
        translate([0, 0, body_height/2])
            scale([0.8, 0.5, 0.7])
            sphere(d=body_height-wall_thickness, $fn=20);
    }
}

// Wing structure with furniture joints
module hawk_wing(side) {
    mirror([side, 0, 0]) {
        // Main wing panel
        difference() {
            // Wing surface
            translate([0, wing_span/4, body_height/2])
                rotate([0, 0, 15])
                scale([1.5, 0.1, 0.4])
                cube([wing_span/2, wing_span/2, body_height/3], center=true);

            // Hollow wing interior
            translate([0, wing_span/4, body_height/2])
                rotate([0, 0, 15])
                scale([1.4, 0.12, 0.35])
                cube([wing_span/2-wall_thickness, wing_span/2-wall_thickness, body_height/3-wall_thickness], center=true);
        }

        // Wing support struts (furniture joints)
        for(i = [1:3]) {
            translate([i*wing_span/8, i*wing_span/6, body_height/2])
                rotate([0, 90, 0])
                cylinder(h=wing_span/4, d=wall_thickness*2, $fn=8);
        }
    }
}

// Head with beak detail
module hawk_head() {
    // Head structure
    difference() {
        translate([body_length/2, 0, body_height*1.2])
            scale([0.8, 0.6, 0.7])
            sphere(d=body_height*0.8, $fn=16);

        // Eye sockets
        translate([body_length/2+body_height/3, body_height/4, body_height*1.2])
            sphere(d=wall_thickness*2, $fn=8);
        translate([body_length/2+body_height/3, -body_height/4, body_height*1.2])
            sphere(d=wall_thickness*2, $fn=8);
    }

    // Beak
    translate([body_length/2+body_height/2, 0, body_height*1.1])
        rotate([0, 0, -15])
        scale([1, 0.4, 0.3])
        cylinder(h=body_height/2, d1=body_height/3, d2=wall_thickness, $fn=6);
}

// Legs and feet (furniture legs)
module hawk_legs() {
    for(side = [-1, 1]) {
        translate([body_length/4, side*body_height/3, 0])
            // Main leg
            cylinder(h=body_height/2, d=wall_thickness*3, $fn=8);

        // Foot structure
        translate([body_length/4, side*body_height/3, 0])
            // Perch bar
            rotate([90, 0, 0])
            cylinder(h=body_height/2, d=wall_thickness*2, $fn=6);
    }
}

// Feather texture details (decorative elements)
module feather_details() {
    // Wing surface pattern
    for(i = [0:5]) {
        for(j = [0:3]) {
            translate([i*body_length/6-10, j*wing_span/8-wing_span/4, body_height/2+body_height/3])
                rotate([90, 0, 45])
                scale([0.3, 0.1, 1])
                cylinder(h=wall_thickness, d1=body_height/8, d2=body_height/16, $fn=3);
        }
    }
}

// Assembly
union() {
    // Main components
    hawk_body();
    hawk_wing(1);
    hawk_wing(-1);
    hawk_head();
    hawk_legs();

    // Structural base
    translate([0, 0, -body_height/4])
        difference() {
            cube([body_length*1.2, wing_span*0.8, wall_thickness*2], center=true);
            cube([body_length*1.1, wing_span*0.7, wall_thickness*3], center=true);
        }

    // Feather decorative details
    feather_details();
}

