cauldron_wall = 2.5;
leg_height = 45;
seat_diameter = 70;
cauldron_base_diameter = 85;
cauldron_height = 30;

// Main stool module
module witch_stool() {
    // Cauldron base (hollow cylinder)
    difference() {
        union() {
            // Outer cauldron
            cylinder(h=cauldron_height, d=cauldron_base_diameter, $fn=100);
            // Bottom dome
            translate([0, 0, cauldron_height])
            scale([1, 1, 0.3])
            sphere(d=cauldron_base_diameter, $fn=100);
        }
        // Hollow out
        translate([0, 0, cauldron_wall])
        cylinder(h=cauldron_height, d=cauldron_base_diameter-2*cauldron_wall, $fn=100);

        translate([0, 0, cauldron_height])
        scale([1, 1, 0.3])
        sphere(d=cauldron_base_diameter-2*cauldron_wall, $fn=100);
    }

    // Three decorative witch legs (bent style)
    for(i=[0:120:359]) {
        rotate([0, 0, i])
        translate([cauldron_base_diameter/2 - leg_thickness - 5, 0, 0])
        rotate([0, 15, 0])
        leg();
    }

    // Seat top (with pentagram detail)
    translate([0, 0, leg_height + cauldron_height - 5])
    difference() {
        // Main seat disk
        cylinder(h=seat_thickness, d=seat_diameter, $fn=100);

        // Pentagram cutout
        color("red")
        translate([0, 0, -1])
        linear_extrude(height=seat_thickness+2)
        pentagram(25);
    }

    // Decorative cauldron handles
    rotate([0, 90, 0])
    translate([cauldron_base_diameter/2 - cauldron_wall, 0, cauldron_height/2])
    cauldron_handle();

    rotate([0, -90, 0])
    translate([cauldron_base_diameter/2 - cauldron_wall, 0, cauldron_height/2])
    cauldron_handle();
}

// Witch-style curved leg
module leg() {
    union() {
        // Main leg post
        translate([0, 0, cauldron_height])
        cylinder(h=leg_height, d=leg_thickness*2, $fn=8);

        // Decorative ball at top
        translate([0, 0, leg_height + cauldron_height])
        sphere(d=leg_thickness*3, $fn=16);

        // Curved foot
        translate([0, 0, cauldron_height/2])
        rotate([0, 90, 0])
        scale([1, 1, 0.5])
        difference() {
            cylinder(h=20, d=leg_thickness*3, $fn=16);
            translate([0, 0, -1])
            cube([40, 20, leg_thickness*3+2], center=true);
        }
    }
}

// Cauldron handle
module cauldron_handle() {
    difference() {
        // Handle base
        union() {
            cylinder(h=20, d=leg_thickness*2, $fn=16);

            // Grip bar
            translate([0, 15, 0])
            rotate([90, 0, 0])
            cylinder(h=30, d=leg_thickness*1.5, $fn=8);
        }

        // Mounting hole
        translate([0, 0, -1])
        cylinder(h=22, d=3, $fn=16);
    }
}

// Pentagram symbol for seat
module pentagram(size) {
    module pentagram_point(r, angle) {
        translate([r*cos(angle), r*sin(angle)])
        circle(d=3);
    }

    for(i=[0:72:359]) {
        assign(a1 = i, a2 = i+72, a3 = i+144)
        hull() {
            pentagram_point(size, a1);
            pentagram_point(size, a2);
        }
    }
}

