        for(y = [-seat_depth/2 + 5, seat_depth/2 - 5])
            translate([x, y, leg_height/2])
                leg();

    // Automaton gears on back
    translate([0, -seat_depth/2 + 5, leg_height + seat_thickness + back_height/2])
        automaton_mechanism();
