// Curved rocker base
module rocker() {
    difference() {
        // Main curved rocker
        rotate([90, 0, 0])
            cylinder(h=rocker_thickness, r=rocker_radius, $fn=50);

        // Hollow center
        translate([0, 0, -rocker_thickness/2])
            cylinder(h=rocker_thickness + 1, r=rocker_radius - 6, $fn=50);

        // Flat bottom surface
        translate([0, -rocker_radius + 10, -rocker_thickness/2 - 1])
            cube([rocker_thickness + 10, 25, rocker_thickness + 2], center=true);
