    // Back frame
    difference() {
        cube([seat_width + 6, 6, back_height], center=true);

        // Hollow back
        cube([seat_width, 8, back_height - 6], center=true);
    }

    // Vertical slats
    for(x = [-seat_width/2 + 10 : 12 : seat_width/2 - 10]) {
        translate([x, 0, 0])
            cube([6, 4, back_height - 8], center=true);
