        union() {
            // Main leg cylinder
            cylinder(h=height, d=diameter, $fn=32);

            // Decorative rings
            for(i = [0.2, 0.4, 0.6, 0.8]) {
                translate([0, 0, height * i])
                    cylinder(h=2, d=diameter * 1.15, $fn=32);
            }
        }

        // Hollow center for 3D printing
        translate([0, 0, -1])
            cylinder(h=height + 2, d=diameter - material_thickness, $fn=32);
    }
}

// Seat slats
module seat_slats(width, depth) {
    for(i = [0:5:depth-5]) {
        translate([-width/2 + material_thickness, -depth/2 + i + 2.5, 0])
            cube([width - material_thickness*2, 5, material_thickness]);
    }
}

// Backrest slats with curved top
module backrest_slats(width, height) {
    for(i = [0:8:width-material_thickness*2-8]) {
        translate([-width/2 + material_thickness + 4 + i, 0, 0]) {
            hull() {
                translate([0, -2, 0])
                    cube([4, 4, height * 0.7]);
                translate([0, -2, height * 0.7])
                    cube([4, 4, height * 0.3]);
