                rotate([0, 45, 0])
                    cylinder(h=8, d=3, $fn=16);
        }

        // Screw hole
        translate([2, 2, 12.5])
            rotate([0, 90, 0])
                cylinder(h=10, d=2, $fn=16);
    }
}

// Main chair assembly
module bard_chair() {
    // Seat frame
    translate([0, 0, seat_height]) {
        difference() {
            // Outer frame
            cube([chair_width, chair_depth, material_thickness*2]);

            // Inner cutout
            translate([material_thickness, material_thickness, -1])
                cube([chair_width - material_thickness*2, chair_depth - material_thickness*2, material_thickness*2 + 2]);
        }

        // Seat slats
        translate([0, 0, material_thickness/2])
            seat_slats(chair_width - material_thickness*2, chair_depth - material_thickness*2);
    }

    // Backrest
    translate([0, chair_depth/2 - material_thickness, seat_height + back_height/2]) {
        difference() {
            // Backrest frame
            cube([chair_width, material_thickness*2, back_height]);

            // Inner cutout
            translate([material_thickness, -1, material_thickness])
                cube([chair_width - material_thickness*2, material_thickness*2 + 2, back_height - material_thickness*2]);
        }

        // Backrest slats
        translate([0, material_thickness, -back_height/2 + material_thickness])
            backrest_slats(chair_width - material_thickness*2, back_height - material_thickness*2);
    }

    // Legs
    positions = [
        [-chair_width/2 + leg_diameter/2, -chair_depth/2 + leg_diameter/2],
        [chair_width/2 - leg_diameter/2, -chair_depth/2 + leg_diameter/2],
        [-chair_width/2 + leg_diameter/2, chair_depth/2 - leg_diameter/2],
        [chair_width/2 - leg_diameter/2, chair_depth/2 - leg_diameter/2]
    ];

    for(pos = positions) {
        translate([pos[0], pos[1], 0])
            turned_leg(seat_height - material_thickness, leg_diameter);
    }

    // Armrests
    armrest_length = 30;
    armrest_height = 10;

    // Left armrest
    translate([-chair_width/2 - 2, -chair_depth/4, seat_height + armrest_height/2]) {
        difference() {
            // Armrest top
            cube([4, armrest_length, armrest_height]);

            // Contoured top
            translate([-1, 0, armrest_height])
                cube([6, armrest_length, 2]);
        }

        // Support
        translate([2, 2, -armrest_height/2])
            armrest_support();
    }

    // Right armrest
    translate([chair_width/2 + 2, -chair_depth/4, seat_height + armrest_height/2]) {
        mirror([1, 0, 0])
            difference() {
                cube([4, armrest_length, armrest_height]);
                translate([-1, 0, armrest_height])
                    cube([6, armrest_length, 2]);
            }

        translate([2, 2, -armrest_height/2])
            armrest_support();
    }

    // Lower support stretchers
    stretcher_height = seat_height * 0.4;

    // Front stretcher
    translate([0, -chair_depth/2 + leg_diameter/2, stretcher_height])
        cube([chair_width - leg_diameter, leg_diameter/1.5, leg_diameter/1.5]);

    // Side stretchers
    translate([-chair_width/2 + leg_diameter/2, 0, stretcher_height])
        rotate([0, 0, 90])
            cube([chair_depth - leg_diameter, leg_diameter/1.5, leg_diameter/1.5]);

    translate([chair_width/2 - leg_diameter/2, 0, stretcher_height])
        rotate([0, 0, 90])
            cube([chair_depth - leg_diameter, leg_diameter/1.5, leg_diameter/1.5]);
}

