// Main stool module
module mushroom_stool() {
    // Mushroom cap with curved top
    union() {
        // Main cap dome
        difference() {
            // Outer dome
            scale([1, 1, 0.6])
            sphere(r = cap_radius);

            // Hollow interior
            translate([0, 0, -cap_thickness])
            scale([1, 1, 0.5])
            sphere(r = cap_radius - cap_thickness);
        }

        // Flatten bottom of cap
        translate([0, 0, -cap_thickness])
        cylinder(h = cap_thickness * 2, r = cap_radius);
    }

    // Stem
    translate([0, 0, -stem_height - cap_thickness])
    difference() {
        // Main stem cylinder
        cylinder(h = stem_height, r = stem_radius);

        // Hollow core
        translate([0, 0, cap_thickness])
        cylinder(h = stem_height - cap_thickness, r = stem_radius - cap_thickness);
    }

    // Gills underneath cap
    for(i = [0:30]) {
        rotate([0, 0, i * 12])
        translate([0, 0, -cap_thickness - 1])
        linear_extrude(height = gill_depth)
        polygon(points = [
            [0, 0],
            [stem_radius + 5, 0],
            [stem_radius + 5, 1],
            [0, 1]
        ]);
    }

    // Root base for stability
    translate([0, 0, -stem_height - cap_thickness])
    for(i = [0:root_count-1]) {
        rotate([0, 0, i * 360/root_count])
        translate([stem_radius * 0.7, 0, 0])
        rotate([90, 0, 0])
        scale([1, 1, 0.3])
        cylinder(h = 15, r1 = 8, r2 = 4);
    }

    // Natural texture rings on stem
    for(z = [5:10:stem_height-5]) {
        translate([0, 0, -z - cap_thickness])
        difference() {
            cylinder(h = 2, r = stem_radius);
            cylinder(h = 2.1, r = stem_radius - cap_thickness);
        }
    }

    // Bark texture on stem
    for(i = [0:15]) {
        rotate([0, 0, i * 24])
        translate([stem_radius - 1, 0, -cap_thickness - stem_height/2])
        scale([1, 0.1, 1])
        sphere(r = 3);
    }
}

// Mushroom spots on cap
module cap_spots() {
    difference() {
        union() {
            // Main cap
            mushroom_stool();

            // Spot bases
            for(i = [0:8]) {
                theta = i * 137.5;  // Golden angle
                r = cap_radius * 0.3 + (i * 3);
                x = r * cos(theta);
                y = r * sin(theta);
                translate([x, y, cap_radius * 0.6 - 5])
                sphere(r = 6);
            }
        }

        // Cut spots flush with cap
        translate([0, 0, cap_radius * 0.6 - 2])
        cube([cap_radius * 3, cap_radius * 3, 10], center = true);
    }
}

