                translate([0, 0, leg_h/2 - wall])
                    cube([wall*4, wall, wall*1.5], center=true);
            }
        // Leather straps
        for (z = [leg_h/4, leg_h*3/4]) {
            translate([x, y, z])
                rotate_extrude()
                    translate([wall*2.5 - wall/2, 0, 0])
                        square([wall, wall*1.2]);
        }
    }
    leg( chair_w/2 - wall*2,  chair_d/2 - wall*2);
    leg(-chair_w/2 + wall*2,  chair_d/2 - wall*2);
    leg( chair_w/2 - wall*2, -chair_d/2 + wall*2);
    leg(-chair_w/2 + wall*2, -chair_d/2 + wall*2);

    // --- Backrest --------------------------------------------------
    // Shield-shaped back with rivets
    difference() {
