        translate([0, -chair_d/2 + wall*2, leg_h + back_h/2])
            sphere(r=wall*3);
    }

    // Rivets along back edges
    for (x = [-chair_w/2 + wall*4, chair_w/2 - wall*4]) {
        translate([x, -chair_d/2 + wall, leg_h + back_h - wall*3])
            sphere(r=wall/1.5);
    }

    // --- Armrests --------------------------------------------------
    // Axe-handle shaped arms
    module arm(x) {
        translate([x, 0, leg_h + wall*3])
            rotate([0, 0, x > 0 ? 15 : -15])
                difference() {
                    cube([wall*3, chair_d/2 - wall*2, wall*2], center=true);
                    // Finger grooves
                    for (i = [0:2])
                        translate([0, chair_d/4 - i*wall*2, 0])
                            cube([wall*3, wall, wall*2], center=true);
                }
    }
    arm( chair_w/2 - wall*2);
    arm(-chair_w/2 + wall*2);

    // --- Decorative skull on top ---------------------------------
    // Simple skull shape
    translate([0, -chair_d/2 + wall*2, leg_h + back_h])
        union() {
            // Cranial dome
            sphere(r=wall*2);
            // Eye sockets
            translate([ wall*1.2, 0, wall*0.5])
                sphere(r=wall*0.7);
            translate([-wall*1.2, 0, wall*0.5])
                sphere(r=wall*0.7);
            // Jaw
            translate([0, 0, -wall*1.5])
                cube([wall*2.5, wall*1.8, wall], center=true);
        }
}

