        for(y = [-30, 30])
            leg(x, y);

    // Support beams between legs
    module support(x1, y1, x2, y2, z) {
        translate([(x1+x2)/2, (y1+y2)/2, z])
        rotate([0, 0, atan2(y2-y1, x2-x1)])
        cube([abs(x2-x1)+thickness, thickness, thickness], center=true);
