            // Axe blade cutout
            translate([x/abs(x)*5, 20, 0])
                rotate([90, 0, 0])
                    cylinder(h=20, d1=15, d2=5, $fn=3);
        }
    }

    arm_rest(-35);
    arm_rest(35);
}

