// Helper module for rounded cuboids
module rounded_cube(size, r) {
    hull() {
        translate([r, r, r]) sphere(r=r);
        translate([r, r, size.z-r]) sphere(r=r);
        translate([r, size.y-r, r]) sphere(r=r);
        translate([r, size.y-r, size.z-r]) sphere(r=r);
        translate([size.x-r, r, r]) sphere(r=r);
        translate([size.x-r, r, size.z-r]) sphere(r=r);
        translate([size.x-r, size.y-r, r]) sphere(r=r);
        translate([size.x-r, size.y-r, size.z-r]) sphere(r=r);
    }
}

// Tree trunk with slight taper and texture rings
module trunk() {
    difference() {
        // Main trunk body
        cylinder(h=trunk_h, r1=trunk_r+2, r2=trunk_r, $fn=32);

        // Center hollow
        translate([0, 0, -1]) cylinder(h=trunk_h+2, r1=trunk_r, r2=trunk_r-2, $fn=32);

        // Growth ring texture
        for(i=[5:10:trunk_h-10]) {
            translate([0, 0, i]) 
                difference() {
                    cylinder(h=1, r=trunk_r+2, $fn=32);
                    cylinder(h=1.5, r=trunk_r-1, $fn=32);
                }
        }
    }
}

// Branch shelf support structure
module branch_shelf(height, angle, length) {
    rotate([0, 0, angle]) {
        // Branch arm
        translate([trunk_r-1, -wall/2, height]) 
            rotate([90, 0, 0])
                cylinder(h=length, r=wall, $fn=8);

        // Shelf
        translate([trunk_r+length, 0, height])
            rotate([0, 0, 90-angle])
                rounded_cube([shelf_d, shelf_w, shelf_t], 2);
    }
}

// Base ring for stability
module base() {
    difference() {
        cylinder(h=base_h, r=base_r, $fn=32);
        cylinder(h=base_h+1, r=base_r-wall, $fn=32);
    }
}

// Assembly
union() {
    // Base
    translate([0, 0, 0]) 
        base();

    // Trunk
    translate([0, 0, base_h])
        trunk();

    // Branch shelves at various heights and angles
    branch_shelf(30, 0, 25);
    branch_shelf(50, 120, 30);
    branch_shelf(70, 240, 22);
    branch_shelf(90, 60, 28);
    branch_shelf(105, 180, 20);

    // Top cap
    translate([0, 0, trunk_h+base_h]) 
        cylinder(h=wall*2, r1=trunk_r+1, r2=0, $fn=32);
}

