module ghost_body() {
    // Main ghost form - rounded bottom with tapered top
    translate([0, 0, 80]) {
        difference() {
            // Outer shell - rounded teardrop shape
            scale([1, 1, 1.2]) 
                sphere(d=90, $fn=50);

            // Hollow interior
            scale([0.9, 0.9, 1.1]) 
                sphere(d=90-wall*2, $fn=50);
        }
    }

    // Ghost bottom - flattened for stability
    translate([0, 0, 35]) 
        difference() {
            cylinder(h=wall*2, d1=85, d2=85, $fn=50);
            cylinder(h=wall*2+1, d1=85-wall*2, d2=85-wall*2, $fn=50);
        }
}

// Ghost eyes (furniture joints)
module ghost_eyes() {
    // Left eye - round opening
    translate([-20, 35, 90]) 
        rotate([0, 10, 0])
            cylinder(h=wall*3, d=12, $fn=30);

    // Right eye - round opening
    translate([20, 35, 90]) 
        rotate([0, 10, 0])
            cylinder(h=wall*3, d=12, $fn=30);
}

// Chair legs inside ghost form
module chair_legs() {
    // Front left leg
    translate([-30, -30, 0]) 
        cylinder(h=40, d=wall*3, $fn=20);

    // Front right leg
    translate([30, -30, 0]) 
        cylinder(h=40, d=wall*3, $fn=20);

    // Back left leg
    translate([-30, 30, 0]) 
        cylinder(h=40, d=wall*3, $fn=20);

    // Back right leg
    translate([30, 30, 0]) 
        cylinder(h=40, d=wall*3, $fn=20);
}

// Chair seat platform
module chair_seat() {
    translate([0, 0, 40]) 
        difference() {
            // Seat top
            cube([60, 60, wall*2], center=true);
            // Hollow cavity
            cube([60-wall*2, 60-wall*2, wall*2+1], center=true);
        }
}

// Chair backrest
module chair_backrest() {
    // Backrest frame
    translate([0, 30, 70]) {
        difference() {
            // Outer frame
            cube([55, wall*2, 60], center=true);
            // Inner opening
            cube([55-wall*4, wall*2+1, 60-wall*4], center=true);
        }
    }

    // Backrest posts
    for (x = [-15, 0, 15]) {
        translate([x, 30, 70]) 
            cylinder(h=60, d=wall*2, $fn=16);
    }
}

// Ghost sheet details
module ghost_sheet() {
    // Flowing bottom edges
    for (i = [0:30:330]) {
        rotate([0, 0, i]) 
            translate([40, 0, 30]) 
                scale([1, 0.3, 1]) 
                    sphere(d=20, $fn=20);
    }

    // Side folds
    translate([-45, 0, 60]) 
        scale([0.5, 1, 1.5]) 
            sphere(d=30, $fn=20);

    translate([45, 0, 60]) 
        scale([0.5, 1, 1.5]) 
            sphere(d=30, $fn=20);
}

// Assembly
union() {
    // Main ghost form
    ghost_body();

    // Ghost eyes
    ghost_eyes();

    // Chair structure visible inside
    union() {
        chair_legs();
        chair_seat();
        chair_backrest();
    }

    // Ghost sheet details
    ghost_sheet();
}

// Zombie Chair - OpenSCAD
// A medium-complexity 3D printable zombie-themed chair
// Overall dimensions: 100mm wide x 110mm deep x 120mm tall

// Parameters
seat_height = 45;
seat_width = 80;
seat_depth = 80;
back_height = 75;
leg_height = seat_height - 10;
wall_thickness = 2;

module zombie_chair() {
    // Chair seat with decay
    translate([0, 0, leg_height])
    difference() {
        cube([seat_width, seat_depth, 10], center=true);
        // Decay holes
        for (i = [0:3]) {
            translate([i*15-22.5, 10, 5])
            rotate([45, 0, 0])
            cylinder(h=15, r=8, $fn=8);
        }
        for (i = [0:2]) {
            translate([i*20-20, -15, 5])
            rotate([-30, 0, 0])
            cylinder(h=15, r=6, $fn=8);
        }
    }

    // Backrest with zombie face
    translate([0, seat_depth/2 - 5, leg_height + back_height/2])
    difference() {
        // Main backrest
        cube([seat_width - 10, wall_thickness, back_height], center=true);

        // Zombie mouth
        translate([0, 0, -10])
        rotate([0, 90, 0])
        cylinder(h=wall_thickness + 2, r1=12, r2=8, $fn=6);

        // Eye sockets
        translate([-15, 0, 15])
        sphere(r=8);
        translate([15, 0, 15])
        sphere(r=8);

        // Decay cracks
        translate([0, 0, 0])
        cube([60, wall_thickness + 2, 5]);
    }

    // Chair legs - bone-like
    module leg(x, y) {
        translate([x, y, leg_height/2])
        difference() {
            // Main leg
            cylinder(h=leg_height, r=8, $fn=8);
            // Carved grooves
            for (i = [0:3]) {
                rotate([0, 0, i*90])
                translate([4, 0, 0])
                cube([8, 2, leg_height], center=true);
            }
        }
    }

    // Position legs
    leg(seat_width/2 - 10, seat_depth/2 - 10);
    leg(-seat_width/2 + 10, seat_depth/2 - 10);
    leg(seat_width/2 - 10, -seat_depth/2 + 10);
    leg(-seat_width/2 + 10, -seat_depth/2 + 10);

    // Armrests - skeletal
    module armrest(x) {
        translate([x, 0, leg_height + 15])
        union() {
            // Main support
            cylinder(h=30, r=6, $fn=6);
            // Joint detail
            translate([0, 0, 15])
            sphere(r=8);
            // Arm piece
            translate([0, seat_depth/2 - 20, 15])
            rotate([90, 0, 0])
            cylinder(h=seat_depth/2 - 10, r=5, $fn=6);
        }
    }

    armrest(seat_width/2);
    armrest(-seat_width/2);

    // Spinal details on back
    for (i = [0:4]) {
        translate([0, seat_depth/2 - 5, leg_height + 10 + i*15])
        difference() {
            sphere(r=6);
            translate([0, 0, -3])
            cube([12, 12, 6], center=true);
        }
    }
}

// Render the chair
zombie_chair();

