// Werewolf Armchair - Realistic Medium Complexity
// Dimensions: 120mm W x 100mm D x 110mm H

// Configuration
wall_thickness = 2;
seat_height = 45;
back_height = 65;

// Main seat base
module seat_base() {
    difference() {
        // Main seat block
        translate([0, 0, seat_height/2])
            cube([80, 90, seat_height], center=true);

        // Hollow interior
        translate([0, 0, seat_height/2])
            cube([76, 86, seat_height-2], center=true);
    }
}

// Werewolf head backrest
module wolf_head() {
    // Head base
    translate([0, -35, seat_height + 30])
    difference() {
        sphere(25);
        translate([0, 10, 0]) sphere(23);
    }

    // Snout
    translate([0, -50, seat_height + 25])
    rotate([10, 0, 0])
    cylinder(h=30, r1=12, r2=8, center=true);

    // Ears
    for(side = [-1, 1]) {
        translate([side*20, -25, seat_height + 45])
        rotate([0, side*10, side*15])
        cylinder(h=20, r1=8, r2=3);
    }

    // Eyes (glow effect holes)
    for(side = [-1, 1]) {
        translate([side*10, -40, seat_height + 32])
        sphere(3);
    }
}

// Armrest with claw details
module armrest(side) {
    // Arm support
    translate([side*45, 0, seat_height/2 + 10])
    difference() {
        cylinder(h=seat_height + 10, r=8);
        translate([0, 0, 2]) cylinder(h=seat_height + 6, r=6);
    }

    // Paw at end
    translate([side*45, 35, seat_height + 5])
    sphere(12);

    // Claws
    for(i = [0:3]) {
        angle = i*30 - 45;
        translate([side*45 + side*cos(angle)*12, 35 + sin(angle)*12, seat_height + 5])
        rotate([90, 0, 0])
        cylinder(h=8, r1=2, r2=0.5);
    }
}

// Legs with paw feet
module leg(pos_x, pos_y) {
    translate([pos_x, pos_y, 0])
    difference() {
        cylinder(h=seat_height - 5, r=6);
        translate([0, 0, 2]) cylinder(h=seat_height - 9, r=4);
    }

    // Paw foot
    translate([pos_x, pos_y, 2])
    sphere(8);

    // Toes
    for(i = [0:3]) {
        angle = i*25 - 37.5;
        translate([pos_x + cos(angle)*8, pos_y + sin(angle)*8, 2])
        rotate([90, 0, 0])
        cylinder(h=5, r1=1.5, r2=0.5);
    }
}

// Fur texture pattern
module fur_texture(base_x, base_y, base_z) {
    for(i = [0:5]) {
        for(j = [0:5]) {
            translate([
                base_x + i*15 - 37.5,
                base_y + j*15 - 37.5,
                base_z + rand(i*100+j*100)*3
            ])
            sphere(1.5 + rand(i*j)*1);
        }
    }
}

// Assembly
union() {
    // Seat structure
    seat_base();

    // Backrest with wolf features
    wolf_head();

    // Backrest extensions (shoulders)
    for(side = [-1, 1]) {
        translate([side*30, -25, seat_height + 20])
        rotate([0, side*5, 0])
        cylinder(h=40, r1=15, r2=10);
    }

    // Armrests
    armrest(1);
    armrest(-1);

    // Legs
    leg(30, 30);
    leg(-30, 30);
    leg(30, -30);
    leg(-30, -30);

    // Fur texture on visible surfaces
    fur_texture(0, 0, seat_height + 2);
    fur_texture(0, -35, seat_height + 25);
}

// Center the model
translate([0, 0, 0]);

// Demon Chest of Drawers - Realistic Style, Medium Complexity
