// Main chest body
module chest_body() {
    difference() {
        // Main body
        translate([0, 0, 50]) cube([120, 60, 100], center=true);

        // Hollow interior
        translate([0, 0, 45]) cube([114, 54, 90], center=true);
    }
}

// Demon head (top ornament)
module demon_head() {
    // Base of head
    translate([0, 0, 105]) cube([40, 30, 20], center=true);

    // Skull shape
    translate([0, 0, 120]) sphere(d=35, $fn=32);

    // Horns
    for(side = [-1, 1]) {
        translate([side * 15, 0, 125]) rotate([0, side * 20, 0]) 
            cylinder(h=25, d1=8, d2=3, center=true, $fn=16);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([side * 10, -12, 120]) sphere(d=6);
    }

    // Mouth
    translate([0, -17, 115]) cube([20, 5, 8], center=true);
