// Demon feet
module demon_feet() {
    for(x = [-50, 50]) {
        for(y = [-25, 25]) {
            translate([x, y, 0]) {
                // Foot base
                cube([20, 15, 20], center=true);

                // Claw toes
                for(toe = [-1, 0, 1]) {
                    translate([toe * 5, 0, 10]) rotate([0, toe * 15, 0])
                        cylinder(h=12, d1=4, d2=2, center=false, $fn=8);
                }
            }
        }
    }
}

// Drawer module
module drawer(width, height, depth, y_pos) {
    // Drawer box
    translate([0, y_pos, 0]) difference() {
        cube([width, depth, height], center=true);
        translate([0, 0, 2]) cube([width-4, depth-4, height-2], center=true);
    }

    // Demon face on drawer front
    translate([0, y_pos + depth/2 - 1, height/2 - 5]) {
        // Eyes
        for(side = [-1, 1]) {
            translate([side * 10, 0, 0]) sphere(d=5);
        }
        // Nose
        translate([0, 0, -5]) sphere(d=3);
        // Mouth
        translate([0, -8, -10]) cube([20, 3, 8], center=true);
    }

    // Handle (horn-shaped)
    translate([0, y_pos + depth/2 + 2, height/2]) rotate([90, 0, 0])
        cylinder(h=15, d1=8, d2=4, center=false, $fn=12);
}

// Side demon wings (structural supports)
module demon_wings() {
    for(side = [-1, 1]) {
        translate([side * 65, 0, 70]) rotate([0, side * 15, 0]) {
            // Wing base
            cube([10, 5, 60], center=true);

            // Wing bones
            for(i = [0:3]) {
                rotate([0, 0, i * 30 - 45]) translate([side * 20, 0, 0])
                    cylinder(h=40, d=3, center=false, $fn=8);
            }
        }
    }
}

// Assembly
union() {
    chest_body();
    demon_head();
    demon_feet();
    demon_wings();

    // Drawers
    drawer(100, 25, 55, 37.5);
    drawer(100, 25, 55, 12.5)
    drawer(100, 25, 55, -12.5)
    drawer(100, 25, 55, -37.5)
}

