// Giant armchair (realistic style, medium complexity)
// Overall dimensions: 120mm x 100mm x 110mm

// Parameters
seat_w = 80;
seat_d = 70;
seat_h = 40;
back_h = 70;
arm_h = 60;
leg_h = 30;
wall = 2;

// Main seat
module seat() {
    difference() {
        // Seat base
        cube([seat_w, seat_d, seat_h], center=true);

        // Hollow out seat
        translate([0, 0, wall/2])
            cube([seat_w-wall*2, seat_d-wall*2, seat_h-wall], center=true);

        // Seat cushion contour (subtle)
        translate([0, 0, seat_h/2-1])
            cube([seat_w-10, seat_d-10, 2], center=true);
    }
}

// Backrest
module backrest() {
    difference() {
        // Main back
        translate([0, -seat_d/2+wall, back_h/2+seat_h/2-wall])
            cube([seat_w, wall*2, back_h], center=true);

        // Hollow
        translate([0, -seat_d/2+wall, back_h/2+seat_h/2-wall])
            cube([seat_w-wall*4, wall*2, back_h-wall*2], center=true);
    }
}

// Armrest support frame
module arm_frame(side) {
    difference() {
        // Vertical post
        translate([side*(seat_w/2-wall), 0, arm_h/2+seat_h/2])
            cube([wall*4, wall*4, arm_h], center=true);

        // Hollow post
        translate([side*(seat_w/2-wall), 0, arm_h/2+seat_h/2])
            cube([wall*2, wall*2, arm_h-wall*2], center=true);
    }
}

// Armrest pad
module arm_pad(side) {
    difference() {
        // Pad
        translate([side*(seat_w/2-wall), seat_d/4, arm_h+seat_h/2-2])
            cube([wall*6, seat_d/2, wall*4], center=true);

        // Hollow pad
        translate([side*(seat_w/2-wall), seat_d/4, arm_h+seat_h/2-2])
            cube([wall*4, seat_d/2-wall*2, wall*2], center=true);
    }
}

// Leg structure
module leg(x_pos, y_pos) {
    difference() {
        // Leg post
        translate([x_pos, y_pos, -leg_h/2])
            cylinder(h=leg_h, d1=wall*3, d2=wall*4, center=true);

        // Hollow leg
        translate([x_pos, y_pos, -leg_h/2])
            cylinder(h=leg_h-wall, d1=wall, d2=wall*2, center=true);
    }
}

// Assembly
union() {
    // Seat
    seat();

    // Backrest
    backrest();

    // Arms
    for (side = [-1, 1]) {
        arm_frame(side);
        arm_pad(side);
    }

    // Legs
    leg(seat_w/2-wall*3, seat_d/2-wall*3);
    leg(-seat_w/2+wall*3, seat_d/2-wall*3);
    leg(seat_w/2-wall*3, -seat_d/2+wall*3);
    leg(-seat_w/2+wall*3, -seat_d/2+wall*3);
}

// Colossus bookshelf - realistic style, medium complexity
// Inspired by ancient stone colossus statues with functional shelves

// Overall dimensions
overall_height = 140;
base_width = 60;
base_depth = 40;

// Wall thickness
wall = 2;

// Base pedestal
module base() {
    difference() {
        // Main base block
        translate([0, 0, 5]) cube([base_width, base_depth, 10], center=true);

        // Hollow out base
        translate([0, 0, 7]) cube([base_width-wall*2, base_depth-wall*2, 10], center=true);

        // Cable management hole in back
        translate([0, base_depth/2-1, 5]) rotate([90, 0, 0]) cylinder(h=wall*2, r=3, center=true);
    }
}

// Torso section with shelves
module torso() {
    // Main torso block
    difference() {
        cube([base_width*0.8, base_depth*0.7, overall_height*0.5], center=true);

        // Hollow center
        translate([0, 0, 5]) cube([base_width*0.8-wall*2, base_depth*0.7-wall*2, overall_height*0.5-wall*2], center=true);

        // Shelf openings
        for (i = [1:3]) {
            translate([0, -base_depth*0.35, overall_height*0.25 - i*30]) 
                cube([base_width*0.8-wall*2, wall*2, 25], center=true);
        }
    }

    // Actual shelves
    for (i = [1:3]) {
        translate([0, 0, overall_height*0.25 - i*30 + 12]) 
            cube([base_width*0.8-wall*2, base_depth*0.7-wall*2, wall], center=true);
    }
}

// Head section with top storage
module head() {
    difference() {
        // Head block
        translate([0, 0, overall_height*0.75]) 
            cube([base_width*0.6, base_depth*0.5, overall_height*0.25], center=true);

        // Storage compartment
        translate([0, 0, overall_height*0.75]) 
            cube([base_width*0.6-wall*2, base_depth*0.5-wall*2, overall_height*0.2], center=true);

        // Access opening in front
        translate([0, -base_depth*0.25+wall, overall_height*0.75]) 
            cube([base_width*0.6-wall*2, wall, overall_height*0.15], center=true);
    }
}

// Decorative columns as arms
module columns() {
    offset = base_width*0.5 + 5;

    for (side = [-1, 1]) {
        translate([side*offset, 0, overall_height*0.4]) {
            difference() {
                // Column body
                cylinder(h=overall_height*0.6, r=4, center=true);

                // Hollow column
                translate([0, 0, -wall]) cylinder(h=overall_height*0.6+wall*2, r=4-wall, center=true);
