// Create tentacle with suckers
module tentacle_shape(length) {
    segments = tentacle_segments;
    segment_length = length / segments;

    for(i = [0:segments-1]) {
        // Main tentacle segment
        translate([0, 0, -i*segment_length])
            cylinder(h=segment_length, 
                    r1=tentacle_r - i*1.2, 
                    r2=tentacle_r - (i+1)*1.2);

        // Suction cups on tentacle
        for(j = [0:2]) {
            cup_z = -i*segment_length - segment_length/2 - j*15;
            cup_angle = j * 120 + i * 30;
            cup_r = tentacle_r*0.8 - i*0.5;

            rotate([0, 90, cup_angle])
                translate([cup_r, 0, cup_z])
                    sucker();
        }
    }

    // Curved tip
    translate([0, 0, -length])
        sphere(d=tentacle_r*1.5);
