        translate([comet_length/3 - i*8, 0, seat_height + 15 + i*3])
        scale([0.8 - i*0.1, 0.3, 0.2])
        cylinder(h=40 - i*5, r1=comet_width/4, r2=comet_width/8);
    }
}

// Comet tail structure (backrest)
module comet_tail() {
    // Main tail curve
    for(i = [0:8]) {
        rotate([0, -10 - i*2, 0])
        translate([comet_length/2 - i*6, 0, seat_height + 25 + i*2])
        scale([1 - i*0.08, 0.7 - i*0.05, 0.3 - i*0.02])
        sphere(r=comet_width/2.5);
    }

