        scale([1, 0.1, 0.5])
        cube([20, wall_thickness*2, 10]);
    }
}

// Support legs (meteor fragments)
module support_legs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([comet_length/4, side*comet_width/3, seat_height/2])
        rotate([0, 10, 0])
        cylinder(h=seat_height - wall_thickness, r1=wall_thickness*2, r2=wall_thickness*3);

        translate([comet_length/4, side*comet_width/3, wall_thickness])
        sphere(r=wall_thickness*2.5);
    }

    // Rear support (larger fragment)
    translate([-comet_length/4, 0, seat_height/2])
    rotate([0, -15, 0])
    cylinder(h=seat_height - wall_thickness, r1=wall_thickness*3, r2=wall_thickness*4);

    translate([-comet_length/4, 0, wall_thickness])
    sphere(r=wall_thickness*3.5);
