// A chair inspired by quantum superposition, featuring entangled frame elements
// Print dimensions: 120mm x 110mm x 150mm

// Overall dimensions
chair_width = 120;
chair_depth = 110;
chair_height = 150;
seat_height = 75;
leg_thickness = 8;

// Module for quantum-inspired leg structure
module quantum_leg(x_pos, y_pos, angle) {
    translate([x_pos, y_pos, 0]) {
        rotate([0, 0, angle]) {
            // Main leg cylinder
            cylinder(h=seat_height, r=leg_thickness/2, $fn=16);

            // Quantum interference pattern rings
            for(i=[1:3:seat_height]) {
                translate([0, 0, i]) {
                    rotate([90, 0, 0]) {
                        scale([1.2, 1.2, 0.5]) 
                        torus(leg_thickness/2 + 1, 1);
                    }
                }
            }
        }
    }
}

// Torus for quantum rings
module torus(outer_r, inner_r) {
    rotate_extrude($fn=32) {
        translate([outer_r, 0, 0]) {
            circle(r=inner_r, $fn=16);
        }
    }
}

// Seat surface with quantum foam texture
module quantum_seat() {
    // Main seat platform
    translate([-chair_width/2, -chair_depth/2, seat_height]) {
        // Base seat
        cube([chair_width, chair_depth, 5]);

        // Quantum foam bumps
        for(x=[-50:10:50]) {
            for(y=[-45:10:45]) {
                translate([x, y, 5]) {
                    // Random quantum state bumps
                    sphere(r=2 + sin(x*y)*1, $fn=8);
                }
            }
        }
    }
}

// Backrest with entangled support pattern
module quantum_backrest() {
    translate([-chair_width/2 + 10, -5, seat_height + 5]) {
        // Main backrest panel
        cube([chair_width - 20, 10, chair_height - seat_height - 5]);

        // Quantum entanglement supports
        for(i=[0:20:chair_width-40]) {
            translate([i, 5, 0]) {
                // Diagonal support struts
                rotate([0, -15, 0]) {
                    cube([2, 10, chair_height - seat_height - 15]);
                }
            }
        }

        // Wave pattern cutouts
        for(z=[10:15:chair_height - seat_height - 20]) {
            translate([5, 0, z]) {
                rotate([0, 90, 0]) {
                    linear_extrude(height=10) {
                        polygon(points=[[0,0],[5,3],[10,0],[5,-3]]);
                    }
                }
            }
        }
    }
}

// Armrests with superposition supports
module quantum_armrest(side) {
    translate([side * (chair_width/2 - 15), 0, seat_height + 20]) {
        // Armrest surface
        cube([15, chair_depth - 20, 5]);

        // Superposition support structure
        translate([7.5, (chair_depth - 20)/2, -20]) {
            // Split support concept
            rotate([0, -10 * side, 0]) {
                cylinder(h=20, r=3, $fn=12);
            }
            rotate([0, 10 * side, 0]) {
                cylinder(h=20, r=3, $fn=12);
            }
        }
    }
}

// Assembly
union() {
    // Four legs with quantum interference
    quantum_leg(-chair_width/2 + 15, -chair_depth/2 + 15, 5);
    quantum_leg(chair_width/2 - 15, -chair_depth/2 + 15, -5);
    quantum_leg(-chair_width/2 + 15, chair_depth/2 - 15, -5);
    quantum_leg(chair_width/2 - 15, chair_depth/2 - 15, 5);

    // Seat assembly
    quantum_seat();

    // Backrest with entangled supports
    quantum_backrest();

    // Armrests
    quantum_armrest(1);
    quantum_armrest(-1);
}

// Realistic Block-Style Stool (Medium Complexity)
// Dimensions: 300mm x 250mm x 450mm (scaled to 100mm height for printing)

$fn = 32;
scale_factor = 100/450;  // Scale to 100mm height for printing

module stool() {
    // Main seat block with rounded edges
    difference() {
        // Base seat block
        translate([0, 0, 440*scale_factor])
        minkowski() {
            cube([280*scale_factor, 230*scale_factor, 20*scale_factor], center=true);
            sphere(5*scale_factor);
        }

        // Hollow out seat for weight reduction
        translate([0, 0, 440*scale_factor])
        cube([260*scale_factor, 210*scale_factor, 25*scale_factor], center=true);
    }

    // Front left leg
    translate([-110*scale_factor, -85*scale_factor, 220*scale_factor])
    rounded_leg(40*scale_factor, 440*scale_factor);

    // Front right leg
    translate([110*scale_factor, -85*scale_factor, 220*scale_factor])
    rounded_leg(40*scale_factor, 440*scale_factor);

    // Back left leg
    translate([-110*scale_factor, 85*scale_factor, 220*scale_factor])
    rounded_leg(40*scale_factor, 440*scale_factor);

    // Back right leg
    translate([110*scale_factor, 85*scale_factor, 220*scale_factor])
    rounded_leg(40*scale_factor, 440*scale_factor);

    // Front stretcher bar
    translate([0, -85*scale_factor, 110*scale_factor])
    cube([200*scale_factor, 30*scale_factor, 20*scale_factor], center=true);

    // Side stretcher bars
    translate([-110*scale_factor, 0, 110*scale_factor])
    rotate([0, 0, 90])
    cube([170*scale_factor, 30*scale_factor, 20*scale_factor], center=true);

    translate([110*scale_factor, 0, 110*scale_factor])
    rotate([0, 0, 90])
    cube([170*scale_factor, 30*scale_factor, 20*scale_factor], center=true);

    // Corner blocks for joints
    for(x = [-1, 1])
    for(y = [-1, 1])
    translate([x*110*scale_factor, y*85*scale_factor, 440*scale_factor])
    corner_block();

    // Wood grain texture on seat top
    translate([0, 0, 455*scale_factor])
    wood_grain(260*scale_factor, 210*scale_factor);
}

module rounded_leg(width, height) {
    difference() {
        // Main leg
        minkowski() {
            cube([width, width, height], center=true);
            sphere(5*scale_factor);
        }

        // Hollow leg for material saving
        cube([width-4*scale_factor, width-4*scale_factor, height+10*scale_factor], center=true);
    }
}

module corner_block() {
    // Triangular support blocks under seat
    difference() {
        translate([0, 0, -10*scale_factor])
        cube([30*scale_factor, 30*scale_factor, 20*scale_factor], center=true);

        // Remove center for weight
        translate([5*scale_factor, 5*scale_factor, -10*scale_factor])
        cube([20*scale_factor, 20*scale_factor, 25*scale_factor], center=true);
    }
}

module wood_grain(width, depth) {
    // Create wood grain pattern with small grooves
    for(i = [-width/2:5*scale_factor:width/2]) {
        translate([i, 0, -1*scale_factor])
        cube([1*scale_factor, depth, 2*scale_factor], center=true);
    }

    for(i = [-depth/2:5*scale_factor:depth/2]) {
        translate([0, i, -1*scale_factor])
        cube([width, 1*scale_factor, 2*scale_factor], center=true);
    }
}

// Render the stool
stool();

// Brick Ottoman / Side Table
// Overall footprint 120x120 mm, height 120 mm
// Wall thickness 2 mm throughout, printable without supports

$fn = 32;  // Smooth curves

module brick_ottoman() {
    // Main hollow brick body
    difference() {
        // Outer brick
        translate([0, 0, 60])
            cube([120, 120, 120], center = true);
        // Inner cavity
        translate([0, 0, 60])
            cube([116, 116, 116], center = true);
    }

    // Top seating / table surface
    difference() {
        // Cushion pad (slightly domed)
        translate([0, 0, 120])
            scale([1, 1, 0.1])
                sphere(d = 120);
        // Remove underside dome
        translate([0, 0, 120 - 2])
            cube([122, 122, 10], center = true);
    }

    // Eight mortar grooves (four on each horizontal face)
    module mortar_groove(x, y, rot) {
        translate([x, y, 60])
            rotate([0, 0, rot])
                cube([120, 4, 4], center = true);
    }
    for (i = [-1, 1]) {
        mortar_groove(i * 30, 0, 0);      // lengthwise
        mortar_groove(0, i * 30, 90);     // crosswise
    }

    // Reinforcement ribs inside for stiffness
    module rib(x, y, rot) {
        translate([x, y, 60])
            rotate([0, 0, rot])
                cube([114, 2, 118], center = true);
    }
    rib(0, 0, 0);
    rib(0, 0, 90);
    rib(0, 0, 45);
    rib(0, 0, -45);

    // Four rounded feet / anti-slip pads
    module foot(x, y) {
        translate([x, y, -1])
            cylinder(h = 4, d = 20, center = false);
    }
    foot(50, 50);
    foot(-50, 50);
    foot(50, -50);
    foot(-50, -50);

    // Optional handle recesses on sides
    module handle_recess(x, y, rot) {
        translate([x, y, 80])
            rotate([0, 0, rot])
                difference() {
                    cube([40, 20, 30], center = true);
                    translate([0, 0, 1])
                        cube([36, 16, 30], center = true);
                }
    }
    handle_recess(60, 0, 90);
    handle_recess(-60, 0, 90);
    handle_recess(0, 60, 0);
    handle_recess(0, -60, 0);
}

brick_ottoman();

// LEGO-Style Office Chair - Realistic Medium Complexity
// Approx 120 mm tall when printed

// Parameters
stud_d = 4.8;           // Standard LEGO stud diameter
stud_h = 1.8;           // Standard LEGO stud height
plate_h = 3.2;          // Standard plate thickness
brick_h = 9.6;          // Standard brick height
wall_t = 2.0;           // Minimum wall thickness for printing

// Chair dimensions in LEGO units
chair_width = 10;       // studs
chair_depth = 10;       // studs
seat_height = 5;        // plates
back_height = 12;       // plates
armrest_height = 8;     // plates

// Real scale factor
scale_factor = 8;

// Helper module for round corners
module round_corner(r, h) {
    difference() {
        cube([r, r, h], center = false);
        translate([r, r, 0])
            cylinder(r = r, h = h, $fn = 16);
    }
}

// LEGO stud module
module lego_stud() {
    cylinder(d = stud_d, h = stud_h, $fn = 16);
}

// LEGO plate/brick with studs on top
module lego_block(width, depth, height, with_studs = true) {
    // Main block
    cube([width * stud_d, depth * stud_d, height * plate_h]);

    // Add studs if needed
    if (with_studs) {
        for (x = [0.5 : width - 1]) {
            for (y = [0.5 : depth - 1]) {
                translate([x * stud_d, y * stud_d, height * plate_h])
                    lego_stud();
            }
        }
    }

    // Hollow bottom with reinforcing ribs
    translate([wall_t, wall_t, -0.1])
        cube([(width * stud_d) - 2*wall_t, (depth * stud_d) - 2*wall_t, wall_t]);

    // Side ribs
    for (x = [2 : 2 : width - 1]) {
        translate([x * stud_d - wall_t/2, wall_t, -0.1])
            cube([wall_t, (depth * stud_d) - 2*wall_t, wall_t]);
    }
    for (y = [2 : 2 : depth - 1]) {
        translate([wall_t, y * stud_d - wall_t/2, -0.1])
            cube([(width * stud_d) - 2*wall_t, wall_t, wall_t]);
    }
}

// Chair seat base
module chair_seat() {
    lego_block(chair_width, chair_depth, seat_height, true);
}

// Chair backrest with ergonomic curve
module chair_back() {
    // Main backrest
    lego_block(chair_width, 2, back_height, true);

    // Curved top edge
    translate([0, 0, back_height * plate_h])
        difference() {
            cube([chair_width * stud_d, 2 * stud_d, 2 * plate_h]);
            translate([chair_width * stud_d, 2 * stud_d, 0])
                rotate([0, 90, 0])
                    cylinder(r = 2 * stud_d, h = chair_width * stud_d, $fn = 20);
        }
}

// Armrest assembly
module armrest(side) {
    // Vertical support
    translate([side * (chair_width - 2) * stud_d, 0, 0])
        lego_block(2, 2, armrest_height, true);

    // Horizontal armrest pad
    translate([side * (chair_width - 3) * stud_d, -1 * stud_d, armrest_height * plate_h])
        lego_block(4, 2, 2, true);
}

// Chair base with 5-star legs
module chair_base() {
    // Central column
    lego_block(4, 4, 4, true);

    // 5-star legs
    for (angle = [0 : 72 : 360]) {
        rotate([0, 0, angle])
            translate([2 * stud_d, -1 * stud_d, 0])
                lego_block(8, 2, 1, false);
    }

    // Caster wheels
    for (angle = [36 : 72 : 360]) {
        rotate([0, 0, angle])
            translate([9 * stud_d, 0, 0])
                sphere(d = 3 * stud_d, $fn = 16);
    }
}

// Gas lift cylinder
module gas_lift() {
    cylinder(d = 3 * stud_d, h = 8 * plate_h, $fn = 16);
}

// Complete chair assembly
module office_chair() {
    // Base
    translate([chair_width * stud_d/2, chair_depth * stud_d/2, 0])
        chair_base();

    // Gas lift
    translate([chair_width * stud_d/2, chair_depth * stud_d/2, 4 * plate_h])
        gas_lift();

    // Seat
    translate([0, 0, 12 * plate_h])
        chair_seat();

    // Backrest
    translate([0, 2 * stud_d, 12 * plate_h])
        chair_back();

    // Armrests
    armrest(1);
    armrest(-1);
}

// Scale to realistic size and render
scale([scale_factor, scale_factor, scale_factor])
    office_chair();

// Column Furniture - Realistic Style with Medium Complexity
// A 3D printable decorative column with structural joints and base

// Overall dimensions (mm)
column_height = 120;
base_diameter = 40;
top_diameter = 35;
wall_thickness = 2;

// Base module with joints
module column_base() {
    // Main base cylinder
    difference() {
        cylinder(h=15, d=base_diameter, $fn=64);

        // Hollow center
        translate([0, 0, 2])
            cylinder(h=14, d=base_diameter - wall_thickness*2, $fn=64);
    }

    // Decorative base ring
    translate([0, 0, 12])
        difference() {
            cylinder(h=5, d=base_diameter + 6, $fn=64);
            cylinder(h=5, d=base_diameter + 2, $fn=64);
        }

    // Joint holes for connecting to frame
    for(i = [0:3]) {
        rotate([0, 0, i*90])
            translate([base_diameter/2 + 2, 0, 7.5])
                rotate([90, 0, 0])
                    cylinder(h=8, d=3, $fn=16);
    }
}

// Shaft with fluting details
module column_shaft() {
    // Main shaft
    difference() {
        cylinder(h=column_height - 30, d1=base_diameter - 4, d2=top_diameter - 2, $fn=64);

        // Hollow center
        translate([0, 0, 2])
            cylinder(h=column_height - 32, d1=base_diameter - 4 - wall_thickness*2, d2=top_diameter - 2 - wall_thickness*2, $fn=64);
    }

    // Fluting details
    for(i = [0:19]) {
        rotate([0, 0, i*18])
            translate([(base_diameter - 4)/2 - 2, 0, 0])
                cube([4, 1, column_height - 30], center=true);
    }
}

// Capital (top) with mounting holes
module column_capital() {
    // Main capital
    difference() {
        cylinder(h=15, d=top_diameter + 8, $fn=64);

        // Hollow center
        translate([0, 0, 2])
            cylinder(h=14, d=top_diameter + 4, $fn=64);
    }

    // Top plate for mounting
    translate([0, 0, 15])
        difference() {
            cylinder(h=5, d=top_diameter + 12, $fn=64);

            // Mounting holes
            for(i = [0:3]) {
                rotate([0, 0, i*90 + 45])
                    translate([top_diameter/2 + 2, 0, 2.5])
                        rotate([0, 0, 0])
                            cylinder(h=6, d=3.2, $fn=16);
            }
        }
}

// Assembly
union() {
    column_base();
    translate([0, 0, 15]) column_shaft();
    translate([0, 0, column_height - 15]) column_capital();
}

// Slab-style coffee table (realistic, medium complexity)
// Dimensions: 100 x 60 x 35 mm (printable size)

$fa = 2;
$fs = 0.5;

// Main tabletop slab
module table_slab() {
    difference() {
        // Main slab body
        cube([100, 60, 8], center=true);

        // Edge chamfers
        translate([0,0,4]) 
            cube([96, 56, 8.1], center=true);

        // Underside recess for weight reduction
        translate([0,0,-2]) 
            cube([90, 50, 4], center=true);
    }
}

// Structural support legs (slab-style)
module leg_pair(x_pos) {
    // Main leg block
    translate([x_pos, 0, -13.5]) 
        cube([4, 50, 27], center=true);

    // Front/back support ribs
    for (y = [-22, 22]) {
        translate([x_pos, y, -13.5]) 
            cube([8, 2, 27], center=true);
    }

    // Side support ribs
    translate([x_pos, 0, -13.5]) 
        cube([2, 46, 27], center=true);
}

// Hidden joinery blocks
module joinery_block(x_pos) {
    translate([x_pos, 0, 0]) {
        difference() {
            cube([8, 46, 4], center=true);
            // Dowel holes
            for (x = [-2, 2])
                for (y = [-15, 0, 15])
                    translate([x, y, 0])
                        cylinder(h=5, r=1.5, center=true);
        }
    }
}

// Surface texture pattern
module surface_texture() {
    // Wood grain grooves
    for (i = [-45:5:45]) {
        translate([0, i, 4.1]) {
            rotate([90, 0, 0])
                cylinder(h=100.2, r=0.3, center=true);
        }
    }

    // End grain detail on sides
    for (i = [-45:3:45]) {
        translate([i, 30.1, 0])
            rotate([0, 90, 0])
                cylinder(h=8.2, r=0.2, center=true);
        translate([i, -30.1, 0])
            rotate([0, 90, 0])
                cylinder(h=8.2, r=0.2, center=true);
    }
}

// Assembly
union() {
    // Tabletop
    table_slab();

    // Leg assemblies
    leg_pair(-45);
    leg_pair(45);

    // Joinery (hidden inside)
    %joinery_block(-45);
    %joinery_block(45);

    // Surface details
    surface_texture();
}

// Realistic Tile Furniture - Medium Complexity
// A modular wooden tile with dovetail joints, realistic proportions and 3D-printable design

$fn = 50;  // Smooth curves for realistic appearance

// Main parameters
tile_size = 100;           // 100mm square tile
tile_height = 12;          // Realistic furniture thickness
wall_thickness = 2;        // 3D printable minimum
wood_texture_depth = 0.3;  // Subtle wood grain

// Dovetail joint parameters
dovetail_width = 20;
dovetail_height = 8;
dovetail_angle = 15;       // Standard dovetail angle

module wood_texture(direction = [1,0,0]) {
    // Creates subtle wood grain texture using small cylinders
    for(i = [-tile_size/2 : 2 : tile_size/2]) {
        translate([i, 0, tile_height/2]) {
            rotate([90, 0, direction[0] * 90])
            scale([1, 0.1, 1])
            cylinder(h = tile_size, r = wood_texture_depth, center = true);
        }
    }
}

module dovetail_male() {
    // Male dovetail connector for tile edges
    union() {
        // Main dovetail body
        translate([0, 0, dovetail_height/2]) {
            difference() {
                cube([dovetail_width, wall_thickness, dovetail_height], center = true);
                // Angled cuts for dovetail shape
                translate([0, wall_thickness/2 + 0.1, 0]) {
                    rotate([0, dovetail_angle, 0])
                    cube([dovetail_width*2, wall_thickness*2, dovetail_height*2], center = true);
                }
                translate([0, -wall_thickness/2 - 0.1, 0]) {
                    rotate([0, -dovetail_angle, 0])
                    cube([dovetail_width*2, wall_thickness*2, dovetail_height*2], center = true);
                }
            }
        }
    }
}

module dovetail_female() {
    // Female dovetail slot
    rotate([0, 0, 180])
    translate([0, 0, -dovetail_height/2])
    dovetail_male();
}

module tile_base() {
    // Main tile body with rounded edges
    difference() {
        // Main tile with rounded top edges
        minkowski() {
            cube([tile_size - wall_thickness*2, tile_size - wall_thickness*2, tile_height - wall_thickness], center = true);
            sphere(r = wall_thickness);
        }

        // Hollow out the center for material efficiency
        translate([0, 0, -tile_height/4])
        cube([tile_size - wall_thickness*6, tile_size - wall_thickness*6, tile_height], center = true);

        // Add support ribs
        for(x = [-tile_size/3, 0, tile_size/3]) {
            for(y = [-tile_size/3, 0, tile_size/3]) {
                translate([x, y, 0])
                cube([wall_thickness*2, wall_thickness*2, tile_height - wall_thickness*2], center = true);
            }
        }
    }
}

module furniture_surface_details() {
    // Adds realistic furniture surface details
    union() {
        // Subtle edge bevel
        difference() {
            cube([tile_size, tile_size, tile_height], center = true);
            cube([tile_size - wall_thickness, tile_size - wall_thickness, tile_height - wall_thickness], center = true);
        }

        // Screw holes in corners for realistic furniture assembly
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * tile_size/3, y * tile_size/3, tile_height/2])
                rotate([180, 0, 0])
                cylinder(h = wall_thickness, r1 = wall_thickness/2, r2 = wall_thickness, center = true);
            }
        }

        // Wood grain texture on top surface
        translate([0, 0, tile_height/2 + 0.1])
        wood_texture([1,0,0]);

        translate([0, 0, tile_height/2 + 0.1])
        rotate([0,0,90])
        wood_texture([0,1,0]);
    }
}

// Assemble the complete tile furniture piece
union() {
    // Main tile body
    tile_base();

    // Surface details
    furniture_surface_details();

    // Dovetail joints on all four sides
    translate([tile_size/2, 0, tile_height/2])
    rotate([0, 90, 0])
    dovetail_male();

    translate([-tile_size/2, 0, tile_height/2])
    rotate([0, -90, 0])
    dovetail_female();

    translate([0, tile_size/2, tile_height/2])
    rotate([90, 0, 0])
    dovetail_male();

    translate([0, -tile_size/2, tile_height/2])
    rotate([-90, 0, 0])
    dovetail_female();
}

// Arch furniture item - decorative bookshelf arch
// Overall dimensions: 120mm wide x 30mm deep x 100mm tall
// Wall thickness: 2mm throughout for 3D printing

$fn = 32;

// Main module
module arch_furniture() {
    // Base platform
    translate([0, 0, 0])
        cube([120, 30, 4], center = true);

    // Left vertical support
    translate([-55, 0, 47])
        cube([10, 26, 90], center = true);

    // Right vertical support
    translate([55, 0, 47])
        cube([10, 26, 90], center = true);

    // Arch top - main curved structure
    difference() {
        // Outer arch
        translate([0, 0, 90])
            rotate([90, 0, 0])
                cylinder(h = 26, r1 = 60, r2 = 60, center = true);

        // Inner arch hollow
        translate([0, 0, 90])
            rotate([90, 0, 0])
                cylinder(h = 30, r1 = 48, r2 = 48, center = true);
    }

    // Shelf supports - left side
    for(i = [0:2]) {
        translate([-55, 0, 20 + i * 25])
            cube([10, 26, 3], center = true);
    }

    // Shelf supports - right side
    for(i = [0:2]) {
        translate([55, 0, 20 + i * 25])
            cube([10, 26, 3], center = true);
    }

    // Shelves
    for(i = [0:2]) {
        translate([0, 0, 24 + i * 25])
            cube([100, 22, 2], center = true);
    }

    // Decorative arch segments
    for(i = [-1:1]) {
        translate([i * 30, 0, 90])
