// Plank-style stool with realistic proportions and furniture details
// Designed for 3D printing with 2mm walls and minimal overhangs

$fn = 32;

// Overall dimensions
seat_width = 100;
seat_depth = 70;
seat_thickness = 8;
leg_height = 45;
leg_thickness = 18;
stretcher_offset = 12;

// Wood plank texture parameters
plank_width = 20;
plank_gap = 1.5;
num_planks = 5;

module rounded_cube(size, radius) {
    // Create a cube with rounded edges
    x = size[0];
    y = size[1];
    z = size[2];

    translate([radius, radius, 0])
    difference() {
        cube([x-2*radius, y-2*radius, z]);

        // Round the edges
        for(i = [0, 1]) {
            for(j = [0, 1]) {
                translate([i*(x-2*radius), j*(y-2*radius), 0])
                cylinder(h=z+2, r=radius, center=true);
            }
        }
    }
}

module wood_plank(length, width, thickness, gap) {
    // Create a single plank with realistic proportions
    difference() {
        rounded_cube([length, width, thickness], 2);

        // Add subtle wood grain texture
        for(i = [1:5:length-5]) {
            translate([i, width/2, thickness-0.5])
            cube([0.5, width-2, 1]);
        }
    }
}

module seat_assembly() {
    // Create the seat from multiple planks
    union() {
        for(i = [0:num_planks-1]) {
            translate([0, i*(plank_width+plank_gap), 0])
            wood_plank(seat_width, plank_width, seat_thickness, plank_gap);
        }

        // Add support frame underneath
        translate([5, 5, -4])
        rounded_cube([seat_width-10, seat_depth-10, 4], 3);
    }
}

module leg_with_brace(pos_x, pos_y) {
    // Create a single leg with stretcher brace
    translate([pos_x, pos_y, -leg_height/2])
    union() {
        // Main leg post
        rounded_cube([leg_thickness, leg_thickness, leg_height], 3);

        // Add stretcher brace
        if(pos_x < seat_width/2) {
            translate([leg_thickness/2, leg_thickness/2, stretcher_offset])
            rotate([0, 90, 0])
            cylinder(h=seat_width-leg_thickness*2, r=leg_thickness/3, center=true);
        }

        if(pos_y < seat_depth/2) {
            translate([leg_thickness/2, leg_thickness/2, stretcher_offset])
            rotate([90, 0, 0])
            cylinder(h=seat_depth-leg_thickness*2, r=leg_thickness/3, center=true);
        }
    }
}

// Assemble the complete stool
union() {
    // Seat
    translate([0, 0, leg_height+seat_thickness/2])
    seat_assembly();

    // Four legs
    leg_offset_x = seat_width/2 - leg_thickness/2;
    leg_offset_y = seat_depth/2 - leg_thickness/2;

    leg_with_brace(-leg_offset_x, -leg_offset_y);
    leg_with_brace(leg_offset_x, -leg_offset_y);
    leg_with_brace(-leg_offset_x, leg_offset_y);
    leg_with_brace(leg_offset_x, leg_offset_y);

    // Add corner brackets for stability
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x*leg_offset_x/2, y*leg_offset_y/2, leg_height-5])
            rotate([0, 0, 45])
            rounded_cube([leg_thickness*1.5, leg_thickness*1.5, 10], 2);
        }
    }
}

