strut_height = 15;
wall_thickness = 2;
hole_diameter = 5;
connector_length = 10;

// Main strut body with hollow core
module strut_body() {
    difference() {
        // Outer body
        cube([strut_length, strut_width, strut_height], center=true);

        // Hollow core
        translate([0, 0, 0])
            cube([strut_length - 2*wall_thickness, strut_width - 2*wall_thickness, strut_height], center=true);
    }
}

// End connector with mounting holes
module end_connector(is_left=true) {
    offset = is_left ? -strut_length/2 - connector_length/2 : strut_length/2 + connector_length/2;

    translate([offset, 0, 0]) {
        // Connector body
        cube([connector_length, strut_width, strut_height], center=true);

        // Mounting holes
        for (i = [-1, 1]) {
            translate([0, i * strut_width/4, 0])
                rotate([0, 90, 0])
                    cylinder(h=connector_length + 2, d=hole_diameter, center=true, $fn=16);
        }

        // Reinforcement ribs
        for (i = [-1, 1]) {
            translate([i * connector_length/4, 0, strut_height/2 - wall_thickness/2])
                cube([wall_thickness, strut_width - 4, wall_thickness], center=true);
        }
    }
}

// Middle support bracket for additional strength
module middle_support() {
    translate([0, 0, strut_height/2 + wall_thickness/2]) {
        // Top flange
        cube([wall_thickness*3, strut_width + 6, wall_thickness], center=true);

        // Side flanges
        for (i = [-1, 1]) {
            translate([0, i * (strut_width/2 + 3), wall_thickness/2])
                cube([wall_thickness*3, wall_thickness, wall_thickness], center=true);
        }

        // Gusset plates
        for (j = [-1, 1]) {
            translate([j * 20, 0, 0]) {
                rotate([0, 90, 0])
                    cylinder(h=strut_height, d=wall_thickness*2, center=true, $fn=8);
            }
        }
    }
}

// Surface texture for grip
module grip_texture() {
    for (i = [0:5:strut_length-10]) {
        translate([i - strut_length/2 + 5, 0, strut_height/2 - wall_thickness]) {
            cube([2, strut_width - 4, 0.5], center=true);
        }
    }
}

// Assembly
union() {
    // Main strut
    strut_body();

    // End connectors
    end_connector(true);
    end_connector(false);

    // Middle support
    middle_support();

    // Grip texture
    grip_texture();
}

