truss_width = 20;
truss_thickness = 2;
joint_diameter = 6;
bolt_head_diameter = 4;
bolt_shaft_diameter = 2;

// Main assembly
module truss_bookshelf() {
    // Left truss frame
    translate([-shelf_length/2 - 5, 0, 0]) {
        truss_frame();
    }

    // Right truss frame
    translate([shelf_length/2 + 5, 0, 0]) {
        mirror([1, 0, 0]) {
            truss_frame();
        }
    }

    // Shelves
    for(z = [30, 60, 90]) {
        shelf(z);
    }

    // Cross-bracing
    cross_bracing();
}

// Truss frame module
module truss_frame() {
    // Vertical members
    for(y = [-shelf_depth/2 + 10, shelf_depth/2 - 10]) {
        // Front post
        translate([0, y, truss_height/2]) {
            cube([truss_thickness, truss_thickness, truss_height], center = true);
        }

        // Back post
        translate([truss_width, y, truss_height/2]) {
            cube([truss_thickness, truss_thickness, truss_height], center = true);
        }
    }

    // Horizontal members
    for(z = [15, 45, 75, 105]) {
        translate([truss_width/2, 0, z]) {
            cube([truss_width - 2, truss_thickness, truss_thickness], center = true);
        }
    }

    // Diagonal braces
    diagonal_brace([0, 0, 0], [truss_width, 0, 30]);
    diagonal_brace([0, 0, 30], [truss_width, 0, 60]);
    diagonal_brace([0, 0, 60], [truss_width, 0, 90]);
    diagonal_brace([0, 0, 90], [truss_width, 0, 120]);
}

// Diagonal brace helper
module diagonal_brace(start, end) {
    length = norm(end - start);
    angle = atan2(end[0] - start[0], end[2] - start[2]);

    translate([(start[0] + end[0])/2, 0, (start[2] + end[2])/2]) {
        rotate([90, 0, -angle]) {
            cylinder(h = length, d = truss_thickness, $fn = 8);
        }
    }
}

// Shelf module
module shelf(z_pos) {
    translate([0, 0, z_pos]) {
        // Main shelf
        cube([shelf_length, shelf_depth, shelf_thickness], center = true);

        // Support brackets
        for(x = [-shelf_length/2 + 15, shelf_length/2 - 15]) {
            translate([x, shelf_depth/2 - 5, -shelf_thickness]) {
                rotate([0, 45, 0]) {
                    cube([10, truss_thickness, 10]);
                }
            }
            translate([x, -shelf_depth/2 + 5, -shelf_thickness]) {
                rotate([0, -45, 0]) {
                    cube([10, truss_thickness, 10]);
                }
            }
        }

        // Bolt holes for mounting
        for(x = [-shelf_length/2 + 20, shelf_length/2 - 20]) {
            for(y = [-shelf_depth/2 + 10, shelf_depth/2 - 10]) {
                translate([x, y, 0]) {
                    cylinder(h = 10, d = bolt_shaft_diameter, center = true, $fn = 8);
                }
            }
        }
    }
}

// Cross-bracing between frames
module cross_bracing() {
    for(z = [15, 45, 75, 105]) {
        // Front cross members
        translate([-shelf_length/2 - 5, shelf_depth/2 - 5, z]) {
            cube([shelf_length + 10, truss_thickness, truss_thickness], center = true);
        }

        // Back cross members
        translate([-shelf_length/2 - 5, -shelf_depth/2 + 5, z]) {
            cube([shelf_length + 10, truss_thickness, truss_thickness], center = true);
        }
    }
}

// Generate the model
truss_bookshelf();

$fs = 0.5;
