        translate([0, 0, 0])
            cube([length-wall*2, depth-wall*2, height-wall*2], center=true);
    }
}

// End caps with slight chamfer
module end_cap(side) {
    difference() {
        // Outer cap
        translate([side*(length/2 - wall), 0, 0])
            cube([wall*2, depth, height], center=true);
        // 45Â° chamfer
        translate([side*(length/2 - wall), 0, height/2])
            rotate([45, 0, 0])
                cube([wall*2, depth*1.5, wall*3], center=true);
    }
}

// Support ribs inside for strength
module support_ribs() {
    for (x = [-length/2 + 10 : 15 : length/2 - 10]) {
        translate([x, 0, 0])
            cube([wall, depth-wall*2, height-wall*2], center=true);
    }
}

// Wooden dowel pin joints
module dowel_pins() {
    pin_diameter = 2.5;
    pin_length = 8;

    // Pins on left end
    for (z = [-height/2 + 5 : 10 : height/2 - 5]) {
        translate([-length/2 - pin_length/2, 0, z])
