    // Pins on right end
    for (z = [-height/2 + 5 : 10 : height/2 - 5]) {
        translate([length/2 + pin_length/2, 0, z])
            rotate([0, 90, 0])
                cylinder(h = pin_length, d = pin_diameter, $fn=16);
